import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const Profile = () => {
  const [orders, setOrders] = useState([]);
  const [user, setUser] = useState({});
  const navigate = useNavigate();

  useEffect(() => {
    const fetchData = async () => {
      const token = localStorage.getItem('token');
      const userId = localStorage.getItem('userId');

      if (!token) {
        navigate('/login');
        return;
      }

      try {
        const config = { headers: { 'x-auth-token': token } };

        // 1. Get User Info
        const userRes = await axios.get(`http://localhost:5000/api/users/${userId}`);
        setUser(userRes.data);

        // 2. Get User's Order History
        // üëá THIS LINE MUST MATCH THE BACKEND ROUTE EXACTLY
        const orderRes = await axios.get('http://localhost:5000/api/orders/myorders', config);
        
        console.log("My Orders:", orderRes.data); // Check console to see if data arrives
        setOrders(orderRes.data);

      } catch (err) {
        console.error("Error fetching data:", err);
      }
    };

    fetchData();
  }, [navigate]);

  return (
    <div style={{ padding: '30px', maxWidth: '800px', margin: '0 auto', fontFamily: 'Arial, sans-serif' }}>
      
      {/* Header */}
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '30px' }}>
        <div>
            <h1 style={{ margin: 0, color: '#2c3e50' }}>üë§ My Profile</h1>
            <p style={{ color: '#777', margin: '5px 0 0 0' }}>Welcome, <strong>{user.name}</strong></p>
        </div>
        <button onClick={() => navigate('/')} style={{ padding: '10px 20px', cursor: 'pointer', background: '#ecf0f1', border: 'none', borderRadius: '5px' }}>
          ‚Üê Back to Menu
        </button>
      </div>

      {/* Order History */}
      <div style={{ background: 'white', padding: '20px', borderRadius: '10px', boxShadow: '0 4px 15px rgba(0,0,0,0.1)' }}>
        <h3 style={{ borderBottom: '1px solid #eee', paddingBottom: '15px', marginTop: 0 }}>üì¶ Order History</h3>

        {orders.length === 0 ? (
          <p style={{ color: '#999', textAlign: 'center', padding: '20px' }}>
            No orders found yet. <br/>
            <span style={{ fontSize: '12px' }}>(If you just ordered, try refreshing the page!)</span>
          </p>
        ) : (
          <div style={{ display: 'flex', flexDirection: 'column', gap: '15px' }}>
            {orders.map((order) => (
              <div key={order._id} style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', padding: '15px', border: '1px solid #eee', borderRadius: '8px', background: '#fcfcfc' }}>
                
                {/* Left: Food Info */}
                <div style={{ display: 'flex', alignItems: 'center', gap: '15px' }}>
                    <img 
                        src={order.recipe ? order.recipe.image : "https://via.placeholder.com/60"} 
                        alt="food" 
                        style={{ width: '60px', height: '60px', objectFit: 'cover', borderRadius: '5px' }} 
                    />
                    <div>
                        <h4 style={{ margin: '0 0 5px 0', color: '#333' }}>
                            {order.recipe ? order.recipe.title : "Mystery Dish"}
                        </h4>
                        <span style={{ fontSize: '12px', color: '#888' }}>
                            Ordered on: {new Date(order.date).toLocaleDateString()}
                        </span>
                    </div>
                </div>

                {/* Right: Status Badge */}
                <div>
                    <span style={{ 
                        padding: '6px 12px', 
                        borderRadius: '20px', 
                        fontSize: '12px', 
                        fontWeight: 'bold',
                        color: 'white',
                        background: order.status === 'Delivered' ? '#2ecc71' : order.status === 'Shipped' ? '#3498db' : '#f39c12'
                    }}>
                        {order.status}
                    </span>
                </div>

              </div>
            ))}
          </div>
        )}
      </div>
      
      {/* Logout */}
      <div style={{ marginTop: '30px', textAlign: 'center' }}>
        <button onClick={() => { localStorage.clear(); navigate('/login'); }} style={{ color: '#e74c3c', background: 'none', border: '1px solid #e74c3c', padding: '10px 30px', borderRadius: '5px', cursor: 'pointer' }}>
            Log Out
        </button>
      </div>

    </div>
  );
};

export default Profile;
