import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom'; // 1. Import useNavigate
import axios from 'axios';

const RecipeDetail = () => {
  const { id } = useParams();
  const navigate = useNavigate(); // 2. Activate the Hook
  const [recipe, setRecipe] = useState(null);

  useEffect(() => {
    const fetchRecipe = async () => {
      try {
        const res = await axios.get(`http://localhost:5000/api/recipes/${id}`);
        setRecipe(res.data);
      } catch (err) {
        console.error("Error fetching recipe:", err);
      }
    };
    fetchRecipe();
  }, [id]);

  const handleOrder = async () => {
    const token = localStorage.getItem('token');
    
    // Check if user is logged in
    if (!token) {
      alert("Please login to order!");
      navigate('/login');
      return;
    }

    try {
      const config = {
        headers: {
          'x-auth-token': token
        }
      };

      // Send Order
      await axios.post('http://localhost:5000/api/orders', { recipeId: id }, config);
      
      // Success Logic
      alert("Order Placed Successfully! üöö");
      
      // Redirect to Profile
      navigate('/profile'); 
      
    } catch (err) {
      console.error("Order Error:", err);
      // Only show failure if the request actually failed (not if navigate failed)
      if (err.response) {
         alert("Order Failed: " + err.response.data.msg);
      } else {
         console.log("Navigation or Network Error occurred after success.");
      }
    }
  };

  if (!recipe) return <div style={{ padding: '50px', textAlign: 'center' }}>Loading...</div>;

  return (
    <div style={{ padding: '20px', maxWidth: '800px', margin: '0 auto', fontFamily: 'Arial, sans-serif' }}>
      
      <button onClick={() => navigate('/')} style={{ marginBottom: '20px', padding: '10px', cursor: 'pointer' }}>
        ‚Üê Back to Menu
      </button>

      <div style={{ border: '1px solid #ddd', borderRadius: '15px', overflow: 'hidden', boxShadow: '0 4px 15px rgba(0,0,0,0.1)' }}>
        <img 
          src="https://images.unsplash.com/photo-1546069901-ba9599a7e63c?w=800&auto=format&fit=crop&q=60" 
          alt={recipe.title} 
          style={{ width: '100%', height: '300px', objectFit: 'cover' }}
        />
        
        <div style={{ padding: '30px' }}>
          <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
            <h1 style={{ margin: 0, color: '#2c3e50' }}>{recipe.title}</h1>
            <span style={{ background: '#27ae60', color: 'white', padding: '5px 15px', borderRadius: '20px', fontWeight: 'bold' }}>
              {recipe.dietaryCategory}
            </span>
          </div>

          <p style={{ color: '#777', marginTop: '10px', fontSize: '16px' }}>
            <strong>Cooking Time:</strong> {recipe.cookingTime} mins | <strong>Difficulty:</strong> {recipe.difficulty}
          </p>

          <hr style={{ border: '0', borderTop: '1px solid #eee', margin: '20px 0' }} />

          <h3>üìù Ingredients</h3>
          <ul style={{ lineHeight: '1.6', color: '#555' }}>
            {recipe.ingredients.map((ing, index) => (
              <li key={index}>{ing}</li>
            ))}
          </ul>

          <h3>üç≥ Instructions</h3>
          <p style={{ lineHeight: '1.6', color: '#555' }}>{recipe.instructions}</p>

          <button 
            onClick={handleOrder}
            style={{ 
              marginTop: '30px', 
              width: '100%', 
              padding: '15px', 
              background: '#e67e22', 
              color: 'white', 
              border: 'none', 
              borderRadius: '8px', 
              fontSize: '18px', 
              fontWeight: 'bold', 
              cursor: 'pointer',
              boxShadow: '0 4px 6px rgba(0,0,0,0.1)'
            }}
          >
            üõí Order Ingredients Kit
          </button>
        </div>
      </div>
    </div>
  );
};

export default RecipeDetail;
