import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

const RecipeList = () => {
  const [recipes, setRecipes] = useState([]);
  
  // Get User Details from LocalStorage
  const userName = localStorage.getItem('name');
  const userId = localStorage.getItem('userId');

  useEffect(() => {
    const fetchRecipes = async () => {
      try {
        const res = await axios.get('http://localhost:5000/api/recipes');
        setRecipes(res.data);
      } catch (err) {
        console.error("Error fetching recipes:", err);
      }
    };
    fetchRecipes();
  }, []);

  return (
    <div style={{ fontFamily: 'Arial, sans-serif', maxWidth: '1000px', margin: '0 auto', padding: '20px' }}>
      
      {/* --- HEADER SECTION --- */}
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '30px', paddingBottom: '20px', borderBottom: '1px solid #eee' }}>
        <div>
          <h1 style={{ color: '#e67e22', margin: 0 }}>üçï Meal Kit Chef</h1>
          {/* Show Name if logged in, otherwise show Guest */}
          <p style={{ color: '#777', margin: '5px 0 0 0' }}>
            {userName ? `Welcome back, Chef ${userName}! üë®‚Äçüç≥` : "Fresh ingredients delivered to your door."}
          </p>
        </div>
        
        {/* Profile / Login Button */}
        <Link to={userId ? "/profile" : "/login"} style={{ textDecoration: 'none' }}>
          <div style={{ display: 'flex', alignItems: 'center', gap: '10px', background: '#f8f9fa', padding: '10px 15px', borderRadius: '30px', border: '1px solid #ddd' }}>
            <span style={{ fontSize: '20px' }}>{userId ? "üë§" : "üîë"}</span>
            <span style={{ color: '#333', fontWeight: 'bold' }}>{userId ? "My Profile" : "Login"}</span>
          </div>
        </Link>
      </div>

      {/* --- RECIPE GRID --- */}
      <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(250px, 1fr))', gap: '30px' }}>
        {recipes.map((recipe) => (
          <div key={recipe._id} style={{ border: '1px solid #ddd', borderRadius: '15px', overflow: 'hidden', boxShadow: '0 4px 8px rgba(0,0,0,0.1)', transition: 'transform 0.2s' }}>
            <img 
              src="https://images.unsplash.com/photo-1546069901-ba9599a7e63c?w=500&auto=format&fit=crop&q=60" 
              alt={recipe.title} 
              style={{ width: '100%', height: '200px', objectFit: 'cover' }}
            />
            <div style={{ padding: '20px' }}>
              <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '10px' }}>
                <h3 style={{ margin: 0, color: '#2c3e50', fontSize: '18px' }}>{recipe.title}</h3>
                <span style={{ background: '#2ecc71', color: 'white', padding: '3px 8px', borderRadius: '10px', fontSize: '12px' }}>{recipe.dietaryCategory}</span>
              </div>
              <p style={{ color: '#777', fontSize: '14px' }}>‚è≥ {recipe.cookingTime} mins | üìâ {recipe.difficulty}</p>
              
              <Link to={`/recipe/${recipe._id}`} style={{ textDecoration: 'none' }}>
                <button style={{ width: '100%', padding: '10px', background: '#e67e22', color: 'white', border: 'none', borderRadius: '5px', cursor: 'pointer', fontWeight: 'bold' }}>
                  View Recipe
                </button>
              </Link>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default RecipeList;
