// export default App;

import React, { useState } from 'react';
import Login from './components/Login';
import RecipeList from './components/RecipeList'; // Import the new component

function App() {
  // Check if a token already exists in storage
  const [token, setToken] = useState(localStorage.getItem('token'));

  return (
    <div className="App">
      {/* If there is NO token, show Login. If there IS a token, show Recipes */}
      {!token ? (
        <Login setToken={setToken} /> 
      ) : (
        <RecipeList />
      )}
    </div>
  );
}

export default App;
