import React, { useState, useEffect } from 'react';
import axios from 'axios';

const RecipeList = () => {
  const [recipes, setRecipes] = useState([]);
  // We use this to track WHICH recipe is currently expanded (by its ID)
  const [expandedRecipeId, setExpandedRecipeId] = useState(null);

  useEffect(() => {
    const fetchRecipes = async () => {
      try {
        const res = await axios.get('http://localhost:5000/api/recipes');
        setRecipes(res.data);
      } catch (err) {
        console.error("Error fetching recipes:", err);
      }
    };
    fetchRecipes();
  }, []);

  // This function flips the switch for a specific recipe
  const toggleRecipe = (id) => {
    if (expandedRecipeId === id) {
      setExpandedRecipeId(null); // Close it if it's already open
    } else {
      setExpandedRecipeId(id); // Open this specific one
    }
  };

  return (
    <div style={{ padding: '20px' }}>
      <h2 style={{ color: '#2ecc71' }}>Today's Menu</h2>
      <div style={{ display: 'flex', gap: '20px', flexWrap: 'wrap', alignItems: 'flex-start' }}>
        {recipes.map((recipe) => (
          <div key={recipe._id} style={styles.card}>
            <h3>{recipe.title}</h3>
            <span style={styles.tag}>{recipe.dietaryCategory}</span>
            <p><strong>‚è≥ Time:</strong> {recipe.cookingTime} mins</p>
            
            {/* Show Nutrients only if closed (to save space) */}
            {expandedRecipeId !== recipe._id && (
              <div style={styles.nutrients}>
                <p>PROTEIN: {recipe.nutrients.protein}</p>
              </div>
            )}

            {/* THE MAGIC PART: If this recipe is expanded, show details */}
            {expandedRecipeId === recipe._id && (
              <div style={{ textAlign: 'left', marginTop: '10px' }}>
                <h4>Ingredients:</h4>
                <ul>
                  {recipe.ingredients.map((ing, index) => (
                    <li key={index}>{ing.quantity} {ing.name}</li>
                  ))}
                </ul>
                <h4>Instructions:</h4>
                <ol>
                  {recipe.instructions.map((step) => (
                    <li key={step.stepNumber}>{step.text}</li>
                  ))}
                </ol>
              </div>
            )}

            <button 
              onClick={() => toggleRecipe(recipe._id)} 
              style={expandedRecipeId === recipe._id ? styles.closeButton : styles.button}
            >
              {expandedRecipeId === recipe._id ? "Close Details" : "View Ingredients"}
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

const styles = {
  card: { border: '1px solid #ddd', padding: '15px', borderRadius: '10px', width: '250px', boxShadow: '0 4px 8px rgba(0,0,0,0.1)', background: 'white' },
  tag: { background: '#eee', padding: '4px 8px', borderRadius: '4px', fontSize: '12px' },
  nutrients: { background: '#f9f9f9', padding: '5px', borderRadius: '5px', margin: '10px 0' },
  button: { background: '#2ecc71', color: 'white', border: 'none', padding: '10px', width: '100%', borderRadius: '5px', cursor: 'pointer', marginTop: '10px' },
  closeButton: { background: '#e74c3c', color: 'white', border: 'none', padding: '10px', width: '100%', borderRadius: '5px', cursor: 'pointer', marginTop: '10px' }
};

export default RecipeList;
