import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import axios from 'axios';
import './RecipeList.css'; 

const RecipeDetail = () => {
  const { id } = useParams();
  const [recipe, setRecipe] = useState(null);

  useEffect(() => {
    const fetchRecipe = async () => {
      try {
        const res = await axios.get(`http://localhost:5000/api/recipes/${id}`);
        setRecipe(res.data);
      } catch (err) {
        console.error("Error fetching recipe:", err);
      }
    };
    fetchRecipe();
  }, [id]);

  if (!recipe) return <div style={{ padding: '20px' }}>Loading delicious details... ü•ò</div>;

  // üëá HELPER FUNCTION: Converts standard YouTube links to Embed links
  const getEmbedUrl = (url) => {
    if (!url) return null;
    let videoId = null;
    
    // Handle standard "youtube.com/watch?v=" URLs
    if (url.includes("v=")) {
        videoId = url.split("v=")[1].split("&")[0];
    } 
    // Handle short "youtu.be/" URLs
    else if (url.includes("youtu.be/")) {
        videoId = url.split("youtu.be/")[1].split("?")[0];
    }

    return `https://www.youtube.com/embed/${videoId}`;
  };

  return (
    <div style={{ maxWidth: '800px', margin: '0 auto', padding: '20px', fontFamily: 'Arial, sans-serif' }}>
      
      <Link to="/" style={{ textDecoration: 'none', color: '#555', fontSize: '16px' }}>
         ‚Üê Back to Menu
      </Link>

      {/* Hero Image */}
      <img 
        src={recipe.image || "https://via.placeholder.com/800x400"} 
        alt={recipe.title} 
        style={{ width: '100%', height: '300px', objectFit: 'cover', borderRadius: '10px', marginTop: '20px' }} 
      />

      <h1 style={{ color: '#2c3e50', fontSize: '36px', marginBottom: '10px' }}>{recipe.title}</h1>
      
      <div style={{ display: 'flex', gap: '15px', color: '#666', marginBottom: '20px', fontSize: '16px' }}>
        <span>‚è± {recipe.cookingTime} mins</span>
        <span>üìä {recipe.difficulty}</span>
        <span>ü•ó {recipe.dietaryCategory}</span>
      </div>

      {/* üëá YOUTUBE VIDEO SECTION (Fixed for "Refused to Connect" error) */}
      {recipe.youtubeUrl && (
        <div style={{ marginBottom: '30px' }}>
            <h3 style={{ borderBottom: '2px solid #e67e22', paddingBottom: '10px', display: 'inline-block' }}>
                üé• Watch Preparation
            </h3>
            <div style={{ position: 'relative', paddingBottom: '56.25%', height: 0, overflow: 'hidden', borderRadius: '10px', boxShadow: '0 4px 12px rgba(0,0,0,0.1)' }}>
                <iframe 
                    src={getEmbedUrl(recipe.youtubeUrl)}
                    title="YouTube video player"
                    frameBorder="0"
                    allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture" 
                    allowFullScreen
                    style={{ position: 'absolute', top: 0, left: 0, width: '100%', height: '100%' }}
                ></iframe>
            </div>
        </div>
      )}

      {/* Ingredients & Instructions Grid */}
      <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '40px' }}>
        
        {/* Ingredients Column */}
        <div>
          <h3 style={{ color: '#e67e22' }}>ü•ï Ingredients</h3>
          <ul style={{ listStyle: 'none', padding: 0 }}>
            {recipe.ingredients.map((ing, index) => (
              <li key={index} style={{ padding: '8px 0', borderBottom: '1px solid #eee' }}>
                 ‚Ä¢ {ing.name}
              </li>
            ))}
          </ul>
        </div>

        {/* Instructions Column */}
        <div>
          <h3 style={{ color: '#e67e22' }}>üìù Instructions</h3>
          <div style={{ display: 'flex', flexDirection: 'column', gap: '15px' }}>
            {recipe.instructions.map((inst, index) => (
              <div key={index} style={{ background: '#f9f9f9', padding: '15px', borderRadius: '8px' }}>
                <strong>Step {inst.step}:</strong>
                <p style={{ margin: '5px 0 0 0', color: '#555' }}>{inst.text}</p>
              </div>
            ))}
          </div>
        </div>

      </div>
      
      {/* Order Button */}
      <div style={{ textAlign: 'center', marginTop: '40px' }}>
        <button style={{ padding: '15px 40px', fontSize: '18px', background: '#27ae60', color: 'white', border: 'none', borderRadius: '30px', cursor: 'pointer', fontWeight: 'bold' }}>
          üõí Order Meal Kit
        </button>
      </div>

    </div>
  );
};

export default RecipeDetail;
