import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';

const RecipeDetail = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [recipe, setRecipe] = useState(null);
  const [error, setError] = useState('');
  const [isSaved, setIsSaved] = useState(false); // New: Track save state

  // 1. Fetch Recipe & Check if Saved
  useEffect(() => {
    const fetchData = async () => {
      try {
        // Get Recipe
        const res = await axios.get(`http://localhost:5000/api/recipes/${id}`);
        setRecipe(res.data);

        // Check if User has saved this (for the Heart color)
        const userId = localStorage.getItem('userId');
        if (userId) {
          const userRes = await axios.get(`http://localhost:5000/api/users/${userId}`);
          // Check if this recipe ID is in their favorites
          const isFav = userRes.data.favorites.some(fav => 
            (typeof fav === 'object' ? fav._id : fav) === id
          );
          setIsSaved(isFav);
        }

      } catch (err) {
        console.error("Fetch Error:", err);
        setError(err.response?.data?.msg || "Failed to load recipe");
      }
    };
    fetchData();
  }, [id]);

  const handleOrder = async () => {
    const token = localStorage.getItem('token');
    if (!token) {
      if(window.confirm("Login to order?")) navigate('/login');
      return;
    }
    try {
      const config = { headers: { 'x-auth-token': token } };
      await axios.post('http://localhost:5000/api/orders', { recipeId: id }, config);
      alert("‚úÖ Order Placed Successfully!");
    } catch (err) {
      alert("‚ùå Order Failed");
    }
  };

  const handleSave = async () => {
    const token = localStorage.getItem('token');
    const userId = localStorage.getItem('userId');

    if (!token) {
      alert("Please login to save.");
      return;
    }

    try {
      await axios.put(`http://localhost:5000/api/users/favorites/${userId}`, { recipeId: id });
      setIsSaved(true); // Make heart red instantly
      alert("‚ù§Ô∏è Recipe Saved!");
    } catch (err) {
      console.error(err);
    }
  };

  if (error) return <div style={{ padding: '50px', textAlign: 'center' }}>‚ö†Ô∏è {error}</div>;
  if (!recipe) return <div style={{ padding: '50px', textAlign: 'center' }}>Loading...</div>;

  return (
    <div style={{ padding: '20px', maxWidth: '800px', margin: '0 auto', fontFamily: 'Arial, sans-serif' }}>
      
      <button onClick={() => navigate('/')} style={{ marginBottom: '20px', padding: '10px' }}>‚Üê Back to Menu</button>

      <div style={{ border: '1px solid #ddd', borderRadius: '15px', overflow: 'hidden', background: 'white' }}>
        
        {/* IMAGE */}
        <img src={recipe.image || "https://placehold.co/800x300"} alt={recipe.title} style={{ width: '100%', height: '300px', objectFit: 'cover' }} />
        
        <div style={{ padding: '30px' }}>
          
          {/* HEADER: Title + Buttons */}
          <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '20px' }}>
            <div>
              <h1 style={{ margin: 0, color: '#2c3e50' }}>{recipe.title}</h1>
              <span style={{ background: '#27ae60', color: 'white', padding: '5px 15px', borderRadius: '20px', fontSize: '14px' }}>
                {recipe.dietaryCategory}
              </span>
            </div>

            <div style={{ display: 'flex', gap: '10px' }}>
              {/* SAVE BUTTON (Changes Color) */}
              <button 
                onClick={handleSave}
                style={{ 
                  background: isSaved ? '#ff7675' : '#ccc', // Red if saved, Grey if not
                  color: 'white', border: 'none', padding: '10px 15px', borderRadius: '5px', cursor: 'pointer', fontWeight: 'bold' 
                }}
              >
                {isSaved ? "‚ù§Ô∏è Saved" : "ü§ç Save"}
              </button>

              <button onClick={handleOrder} style={{ background: '#e67e22', color: 'white', border: 'none', padding: '10px 20px', borderRadius: '5px', cursor: 'pointer', fontWeight: 'bold' }}>
                üõí Order Kit
              </button>
            </div>
          </div>

          <p><strong>‚è≥ Time:</strong> {recipe.cookingTime} mins | <strong>üìä Difficulty:</strong> {recipe.difficulty}</p>

          {/* INGREDIENTS */}
          <h3>üìù Ingredients</h3>
          <ul>
            {recipe.ingredients.map((ing, i) => <li key={i}>{typeof ing === 'object' ? ing.name : ing}</li>)}
          </ul>

          {/* INSTRUCTIONS */}
          <h3>üç≥ Instructions</h3>
          {Array.isArray(recipe.instructions) ? (
            <ol>
              {recipe.instructions.map((step, i) => <li key={i}>{typeof step === 'object' ? step.text : step}</li>)}
            </ol>
          ) : <p>{recipe.instructions}</p>}

          {/* VIDEO PLAYER (Only shows if URL exists) */}
          {recipe.youtubeUrl && (
            <div style={{ marginTop: '40px', borderTop: '1px solid #eee', paddingTop: '20px' }}>
              <h3>üé• Watch Video</h3>
              <div style={{ position: 'relative', paddingBottom: '56.25%', height: 0 }}>
                <iframe 
                  style={{ position: 'absolute', top: 0, left: 0, width: '100%', height: '100%' }}
                  src={recipe.youtubeUrl.replace("watch?v=", "embed/")} 
                  title="Video" frameBorder="0" allowFullScreen
                ></iframe>
              </div>
            </div>
          )}

        </div>
      </div>
    </div>
  );
};

export default RecipeDetail;
