import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const Login = ({ setToken }) => {
  const navigate = useNavigate();
  
  // Modes
  const [isRegistering, setIsRegistering] = useState(false);
  const [useOtp, setUseOtp] = useState(false); 
  const [otpSent, setOtpSent] = useState(false); 

  // Form Data
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [otp, setOtp] = useState(''); 
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');

    try {
      let res;

      // --- SCENARIO A: OTP FLOW ---
      if (useOtp) {
        if (!otpSent) {
          // Step 1: Request OTP
          await axios.post('http://localhost:5000/api/users/send-otp', { email });
          setOtpSent(true);
          alert(`OTP Sent to ${email}. (Check your VS Code Terminal!)`);
          return; 
        } else {
          // Step 2: Verify OTP
          res = await axios.post('http://localhost:5000/api/users/verify-otp', { email, otp });
        }
      } 
      
      // --- SCENARIO B: PASSWORD FLOW ---
      else {
        if (isRegistering) {
          res = await axios.post('http://localhost:5000/api/users/register', { name, email, password });
        } else {
          res = await axios.post('http://localhost:5000/api/users/login', { email, password });
        }
      }

      // Success! Save Data
      localStorage.setItem('token', res.data.token);
      localStorage.setItem('userId', res.data.user.id);
      localStorage.setItem('name', res.data.user.name);
      setToken(res.data.token);
      navigate('/');
      
    } catch (err) {
      console.error("Auth Error:", err);
      setError(err.response?.data?.msg || "Authentication failed.");
    }
  };

  return (
    <div style={{ maxWidth: '400px', margin: '50px auto', padding: '30px', border: '1px solid #ddd', borderRadius: '10px', boxShadow: '0 4px 10px rgba(0,0,0,0.1)', fontFamily: 'Arial' }}>
      
      <h2 style={{ textAlign: 'center', color: '#2c3e50' }}>
        {useOtp ? 'ðŸ”‘ OTP Login' : (isRegistering ? 'Create Account' : 'Welcome Back')}
      </h2>
      
      {error && <div style={{ background: '#ffeded', color: '#e74c3c', padding: '10px', borderRadius: '5px', marginBottom: '15px', fontSize: '14px', textAlign: 'center' }}>{error}</div>}

      <form onSubmit={handleSubmit} style={{ display: 'flex', flexDirection: 'column', gap: '15px' }}>
        
        {/* Name Field (Only for Password Register) */}
        {!useOtp && isRegistering && (
          <input type="text" placeholder="Full Name" value={name} onChange={(e) => setName(e.target.value)} required style={styles.input} />
        )}

        <input type="email" placeholder="Email Address" value={email} onChange={(e) => setEmail(e.target.value)} required style={styles.input} />
        
        {/* Toggle: Show Password OR OTP Input */}
        {!useOtp ? (
          <input type="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} required style={styles.input} />
        ) : (
          otpSent && (
            <input type="text" placeholder="Enter 6-digit Code" value={otp} onChange={(e) => setOtp(e.target.value)} required style={{...styles.input, textAlign: 'center', letterSpacing: '5px', fontSize: '20px'}} />
          )
        )}

        <button type="submit" style={styles.button}>
          {useOtp 
            ? (otpSent ? 'Verify & Login' : 'Send OTP Code') 
            : (isRegistering ? 'Sign Up' : 'Login')
          }
        </button>
      </form>

      {/* Toggles */}
      <div style={{ textAlign: 'center', marginTop: '20px', borderTop: '1px solid #eee', paddingTop: '15px', display: 'flex', flexDirection: 'column', gap: '10px' }}>
        
        {/* Toggle OTP vs Password */}
        <button 
          onClick={() => {
            setUseOtp(!useOtp);
            setOtpSent(false);
            setError('');
          }} 
          style={{ background: 'none', border: 'none', color: '#555', cursor: 'pointer', fontSize: '13px' }}
        >
          {useOtp ? 'Use Password instead' : 'âœ¨ Try Passwordless (OTP) Login'}
        </button>

        {/* Toggle Login vs Register (Only if using password) */}
        {!useOtp && (
          <button 
            onClick={() => { setIsRegistering(!isRegistering); setError(''); }}
            style={{ background: 'none', border: 'none', color: '#3498db', cursor: 'pointer', fontWeight: 'bold', textDecoration: 'underline' }}
          >
            {isRegistering ? 'Login here' : 'Create an account'}
          </button>
        )}
      </div>
    </div>
  );
};

const styles = {
  input: { padding: '12px', borderRadius: '5px', border: '1px solid #ccc' },
  button: { padding: '12px', background: '#27ae60', color: 'white', border: 'none', borderRadius: '5px', cursor: 'pointer', fontWeight: 'bold', fontSize: '16px' }
};

export default Login;
