import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import axios from 'axios';

const RecipeDetail = () => {
  const { id } = useParams();
  const [recipe, setRecipe] = useState(null);
  const [isFavorite, setIsFavorite] = useState(false); // State for the heart color

  const userId = localStorage.getItem('userId'); // Get our ID from storage

  useEffect(() => {
    // 1. Fetch the Recipe Data
    const fetchRecipe = async () => {
      try {
        const res = await axios.get('http://localhost:5000/api/recipes');
        const foundRecipe = res.data.find((r) => r._id === id);
        setRecipe(foundRecipe);
      } catch (err) {
        console.error("Error fetching recipe:", err);
      }
    };

    // 2. Fetch User Data to see if they already like this recipe
    const checkFavoriteStatus = async () => {
      if (!userId) return; // If not logged in, skip this
      try {
        const res = await axios.get(`http://localhost:5000/api/users/${userId}`);
        const userFavorites = res.data.favorites || [];
        // If the recipe ID is in the list, set heart to true
        if (userFavorites.includes(id)) {
          setIsFavorite(true);
        }
      } catch (err) {
        console.error("Error fetching user favorites:", err);
      }
    };

    fetchRecipe();
    checkFavoriteStatus();
  }, [id, userId]);

  // 3. Handle the Click
  const toggleFavorite = async () => {
    if (!userId) {
      alert("Please login to save recipes!");
      return;
    }

    try {
      // Send the request to backend
      await axios.put(`http://localhost:5000/api/users/${userId}/favorites`, {
        recipeId: id
      });
      // Flip the state (Red <-> Empty)
      setIsFavorite(!isFavorite);
    } catch (err) {
      console.error("Error updating favorites:", err);
    }
  };

  if (!recipe) return <div style={{ padding: '50px', textAlign: 'center' }}>Loading Chef's Secret... üç≥</div>;

  return (
    <div style={{ maxWidth: '800px', margin: '0 auto', padding: '20px', fontFamily: 'Arial, sans-serif' }}>
      
      {/* Navigation & Favorite Button */}
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '20px' }}>
        <Link to="/" style={{ textDecoration: 'none', color: '#555', fontSize: '18px', fontWeight: 'bold' }}>
          ‚Üê Back to Menu
        </Link>
        
        {/* THE MAGIC HEART BUTTON */}
        <button 
          onClick={toggleFavorite}
          style={{ 
            background: isFavorite ? '#e74c3c' : 'transparent', // Red if favorite, clear if not
            color: isFavorite ? 'white' : '#e74c3c', 
            border: '1px solid #e74c3c', 
            padding: '8px 15px', 
            borderRadius: '5px', 
            cursor: 'pointer',
            transition: 'background 0.2s'
          }}
        >
          {isFavorite ? "‚ù§Ô∏è Saved" : "ü§ç Save to Favorites"}
        </button>
      </div>

      <div style={{ border: '1px solid #ddd', borderRadius: '15px', overflow: 'hidden', boxShadow: '0 8px 16px rgba(0,0,0,0.1)' }}>
        
        <img 
          src="https://images.unsplash.com/photo-1546069901-ba9599a7e63c?w=1000&auto=format&fit=crop&q=80" 
          alt={recipe.title} 
          style={{ width: '100%', height: '300px', objectFit: 'cover' }}
        />

        <div style={{ padding: '30px' }}>
          
          <h1 style={{ color: '#2c3e50', margin: '0 0 10px 0' }}>{recipe.title}</h1>
          <div style={{ display: 'flex', gap: '15px', marginBottom: '20px' }}>
            <span style={{ background: '#2ecc71', color: 'white', padding: '5px 10px', borderRadius: '5px' }}>{recipe.dietaryCategory}</span>
            <span style={{ background: '#eee', color: '#555', padding: '5px 10px', borderRadius: '5px' }}>‚è≥ {recipe.cookingTime} mins</span>
          </div>

          <hr style={{ border: 'none', borderTop: '1px solid #eee', margin: '20px 0' }} />

          <div style={{ display: 'flex', flexWrap: 'wrap', gap: '40px', marginBottom: '40px' }}>
            <div style={{ flex: 1, minWidth: '250px' }}>
              <h3 style={{ color: '#e74c3c' }}>Ingredients</h3>
              <ul style={{ lineHeight: '1.8', color: '#444' }}>
                {recipe.ingredients.map((ing, index) => (
                  <li key={index}><strong>{ing.quantity}</strong> {ing.name}</li>
                ))}
              </ul>
            </div>

            <div style={{ flex: 1.5, minWidth: '250px' }}>
              <h3 style={{ color: '#e74c3c' }}>Instructions</h3>
              <ol style={{ lineHeight: '1.8', color: '#444' }}>
                {recipe.instructions.map((step) => (
                  <li key={step.stepNumber} style={{ marginBottom: '10px' }}>{step.text}</li>
                ))}
              </ol>
            </div>
          </div>

          <div style={{ marginTop: '30px', paddingTop: '20px', borderTop: '1px solid #eee' }}>
            <h3 style={{ color: '#2c3e50', display: 'flex', alignItems: 'center', gap: '10px' }}>
              üì∫ Watch How to Make It
            </h3>
            
            <div style={{ position: 'relative', paddingBottom: '56.25%', height: 0, overflow: 'hidden', borderRadius: '10px', marginTop: '15px' }}>
              <iframe 
                style={{ position: 'absolute', top: 0, left: 0, width: '100%', height: '100%' }}
                src="https://www.youtube.com/embed/v7CN0YhkCis" 
                title="YouTube video player" 
                frameBorder="0" 
                allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture" 
                allowFullScreen
              ></iframe>
            </div>
          </div>

        </div>
      </div>
    </div>
  );
};

export default RecipeDetail;
