import React, { useState, useEffect } from 'react';
import axios from 'axios';

const AdminDashboard = () => {
  const [orders, setOrders] = useState([]);
  
  // Security State
  const [isAdmin, setIsAdmin] = useState(false);
  const [secretCode, setSecretCode] = useState("");

  const handleLogin = () => {
    // üëá NEW PASSWORD SET HERE
    if (secretCode === "chef123") {
      setIsAdmin(true);
    } else {
      alert("Wrong Password! Try 'chef123'");
    }
  };

  const getConfig = () => {
    const token = localStorage.getItem('token');
    return {
      headers: { 'x-auth-token': token }
    };
  };

  useEffect(() => {
    if (isAdmin) {
      const fetchAllOrders = async () => {
        try {
          const res = await axios.get('http://localhost:5000/api/orders', getConfig());
          setOrders(res.data);
        } catch (err) {
          console.error("Error fetching orders:", err);
          // üëá I DISABLED THE SECURITY KICK-OUT HERE
          // Now, even if the backend says "403 Forbidden", you stay on the page.
          if(err.response &&( err.response.status === 401 || err.response.status === 403)) {
              console.log("Backend said you are not admin, but I am letting you stay.");
          }
        }
      };
      fetchAllOrders();
    }
  }, [isAdmin]);

  const updateStatus = async (orderId, newStatus) => {
    try {
      await axios.put(`http://localhost:5000/api/orders/${orderId}`, { status: newStatus }, getConfig());
      setOrders(orders.map(order => 
        order._id === orderId ? { ...order, status: newStatus } : order
      ));
    } catch (err) {
      console.error("Error updating status:", err);
      alert("Failed to update status. Check console.");
    }
  };

  const getStatusColor = (status) => {
    if (status === 'Delivered') return '#2ecc71'; // Green
    if (status === 'Shipped') return '#3498db';   // Blue
    return '#f39c12';                             // Orange
  };

  if (!isAdmin) {
    return (
      <div style={{ padding: '50px', textAlign: 'center', fontFamily: 'Arial' }}>
        <h1 style={{ color: '#e74c3c' }}>üîí Chef's Login</h1>
        <p>Password is: <b>chef123</b></p>
        <input 
            type="password" 
            placeholder="Enter Password" 
            value={secretCode} 
            onChange={(e) => setSecretCode(e.target.value)} 
            style={{ padding: '10px', fontSize: '16px' }} 
        />
        <br />
        <button onClick={handleLogin} style={{ marginTop: '10px', padding: '10px 20px', cursor: 'pointer', fontSize: '16px', background: '#333', color: 'white', border: 'none', borderRadius: '5px' }}>
            Unlock Dashboard
        </button>
      </div>
    );
  }

  return (
    <div style={{ padding: '30px', maxWidth: '1000px', margin: '0 auto', fontFamily: 'Arial, sans-serif' }}>
      
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '30px' }}>
        <h1 style={{ color: '#2c3e50', margin: 0 }}>üëÆ‚Äç‚ôÇÔ∏è Chef's Control Room</h1>
        <button onClick={() => setIsAdmin(false)} style={{ background: '#e74c3c', color: 'white', border: 'none', padding: '10px', borderRadius: '5px', cursor: 'pointer' }}>Lock</button>
      </div>

      <div style={{ background: 'white', boxShadow: '0 4px 15px rgba(0,0,0,0.1)', borderRadius: '10px', overflow: 'hidden' }}>
        <table style={{ width: '100%', borderCollapse: 'collapse', textAlign: 'left' }}>
          <thead style={{ background: '#f8f9fa', borderBottom: '2px solid #eee' }}>
            <tr>
              <th style={{ padding: '15px' }}>Order ID</th>
              <th style={{ padding: '15px' }}>Customer</th>
              <th style={{ padding: '15px' }}>Dish</th>
              <th style={{ padding: '15px' }}>Status</th>
              <th style={{ padding: '15px' }}>Actions</th>
            </tr>
          </thead>
          <tbody>
            {orders.length === 0 ? (
                <tr><td colSpan="5" style={{padding:'20px', textAlign:'center', color:'#777'}}>No orders found (or Access Denied by Server)</td></tr>
            ) : (
                orders.map((order) => (
                <tr key={order._id || Math.random()} style={{ borderBottom: '1px solid #eee' }}>
                    <td style={{ padding: '15px', color: '#777', fontSize: '12px' }}>
                    {order._id ? order._id.substring(order._id.length - 6) : "Error"}...
                    </td>
                    <td style={{ padding: '15px', fontWeight: 'bold' }}>
                    {order.user ? order.user.name : "Unknown"}
                    </td>
                    <td style={{ padding: '15px' }}>
                    {order.recipe ? order.recipe.title : "Unknown"}
                    </td>
                    <td style={{ padding: '15px' }}>
                    <span style={{ background: getStatusColor(order.status), color: 'white', padding: '5px 10px', borderRadius: '15px', fontSize: '12px', fontWeight: 'bold' }}>
                        {order.status || "Ordered"}
                    </span>
                    </td>
                    <td style={{ padding: '15px' }}>
                    <select 
                        onChange={(e) => updateStatus(order._id, e.target.value)} 
                        value={order.status || "Ordered"}
                        disabled={order.status === 'Delivered'} 
                        style={{ 
                        padding: '5px', 
                        borderRadius: '5px', 
                        border: '1px solid #ddd',
                        cursor: order.status === 'Delivered' ? 'not-allowed' : 'pointer',
                        opacity: order.status === 'Delivered' ? 0.6 : 1
                        }}
                    >
                        <option value="Ordered">Ordered</option>
                        <option value="Shipped">Shipped</option>
                        <option value="Delivered">Delivered</option>
                    </select>
                    </td>
                </tr>
                ))
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default AdminDashboard;
