import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import './RecipeList.css'; 

const RecipeList = () => {
  const [recipes, setRecipes] = useState([]);

  useEffect(() => {
    const fetchRecipes = async () => {
      try {
        const res = await axios.get('http://localhost:5000/api/recipes');
        setRecipes(res.data);
      } catch (err) {
        console.error("Error fetching recipes:", err);
      }
    };
    fetchRecipes();
  }, []);

  return (
    <div style={{ maxWidth: '1000px', margin: '0 auto', padding: '20px', fontFamily: 'Arial, sans-serif' }}>
      
      {/* üëá NAVIGATION BAR (Admin & Profile Links) */}
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '20px' }}>
         <div style={{ fontWeight: 'bold', fontSize: '20px', color: '#27ae60' }}>
             üçÉ Quick Dish
         </div>
         <div>
            <Link to="/profile" style={{ marginRight: '15px', textDecoration: 'none', color: '#555', fontWeight: 'bold' }}>üë§ My Profile</Link>
            <Link to="/admin" style={{ textDecoration: 'none', color: '#e74c3c', fontWeight: 'bold' }}>üëÆ‚Äç‚ôÇÔ∏è Chef Area</Link>
         </div>
      </div>

      {/* üëá HERE IS THE TITLE CHANGE YOU REQUESTED üëá */}
      <h1 style={{ textAlign: 'center', color: '#2c3e50', marginTop: '40px', marginBottom: '10px', textTransform: 'uppercase', letterSpacing: '1px' }}>
        Quick Dish
      </h1>
      <h3 style={{ textAlign: 'center', color: '#7f8c8d', margin: '0 0 40px 0', fontWeight: 'normal', fontStyle: 'italic' }}>
        No Chef? No Stress.
      </h3>
      {/* üëÜ END TITLE CHANGE üëÜ */}

      {/* Recipe Grid */}
      <div className="recipe-grid">
        {recipes.map((recipe) => (
          <div key={recipe._id} className="recipe-card">
            <img 
              src={recipe.image || "https://via.placeholder.com/300x200"} 
              alt={recipe.title} 
              className="recipe-image" 
            />
            <div className="recipe-info">
              <h3>{recipe.title}</h3>
              <div style={{ display: 'flex', justifyContent: 'space-between', color: '#777', fontSize: '14px', marginBottom: '10px' }}>
                <span>‚è± {recipe.cookingTime}m</span>
                <span>{recipe.difficulty}</span>
              </div>
              
              <Link to={`/recipe/${recipe._id}`}>
                <button className="view-btn">View Recipe</button>
              </Link>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default RecipeList;
