import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';

const RecipeDetail = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [recipe, setRecipe] = useState(null);
  const [error, setError] = useState(''); // Store error message

  useEffect(() => {
    const fetchRecipe = async () => {
      try {
        console.log("Fetching ID:", id); // Log ID to console
        const res = await axios.get(`http://localhost:5000/api/recipes/${id}`);
        setRecipe(res.data);
      } catch (err) {
        console.error("Fetch Error:", err);
        // Show the actual error message on screen
        setError(err.response?.data?.msg || err.message || "Failed to load recipe");
      }
    };
    fetchRecipe();
  }, [id]);

  const handleOrder = async () => {
    const token = localStorage.getItem('token');
    if (!token) {
      alert("Please login to order!");
      navigate('/login');
      return;
    }
    try {
      const config = { headers: { 'x-auth-token': token } };
      await axios.post('http://localhost:5000/api/orders', { recipeId: id }, config);
      alert("Order Placed Successfully! üöö");
      navigate('/profile'); 
    } catch (err) {
      alert("Order Failed: " + (err.response?.data?.msg || "Server Error"));
    }
  };

  // --- ERROR STATE ---
  if (error) return (
    <div style={{ padding: '50px', textAlign: 'center', color: 'red' }}>
      <h2>‚ö†Ô∏è Error Loading Recipe</h2>
      <p>{error}</p>
      <button onClick={() => navigate('/')}>Back to Menu</button>
    </div>
  );

  // --- LOADING STATE ---
  if (!recipe) return <div style={{ padding: '50px', textAlign: 'center' }}>Loading Recipe ID: {id}...</div>;

  return (
    <div style={{ padding: '20px', maxWidth: '800px', margin: '0 auto', fontFamily: 'Arial, sans-serif' }}>
      <button onClick={() => navigate('/')} style={{ marginBottom: '20px', padding: '10px', cursor: 'pointer' }}>
        ‚Üê Back to Menu
      </button>
      <div style={{ border: '1px solid #ddd', borderRadius: '15px', overflow: 'hidden', boxShadow: '0 4px 15px rgba(0,0,0,0.1)' }}>
        <img src="https://images.unsplash.com/photo-1546069901-ba9599a7e63c?w=800&auto=format&fit=crop&q=60" alt={recipe.title} style={{ width: '100%', height: '300px', objectFit: 'cover' }} />
        <div style={{ padding: '30px' }}>
          <h1 style={{ margin: 0, color: '#2c3e50' }}>{recipe.title}</h1>
          <p><strong>Cooking Time:</strong> {recipe.cookingTime} mins | <strong>Difficulty:</strong> {recipe.difficulty}</p>
          <hr />
          <h3>üìù Ingredients</h3>
          <ul style={{ lineHeight: '1.6', color: '#555' }}>
            {recipe.ingredients.map((ing, index) => (
              <li key={index}>
                {/* CHECK: If it's an object, show the name. If it's a string, show it directly. */}
                {typeof ing === 'object' ? ing.name : ing}
              </li>
            ))}
          </ul>
          <h3>üç≥ Instructions</h3>
          {Array.isArray(recipe.instructions) ? (
            <ol style={{ lineHeight: '1.6', color: '#555', paddingLeft: '20px' }}>
              {recipe.instructions.map((step, index) => (
                <li key={index} style={{ marginBottom: '10px' }}>
                  {/* Check: Is it an object (step.text)? Or just a string? */}
                  {typeof step === 'object' ? step.text : step}
                </li>
              ))}
            </ol>
          ) : (
            /* Fallback: If it's just one big string, print it */
            <p style={{ lineHeight: '1.6', color: '#555' }}>{recipe.instructions}</p>
          )}
          <p>{recipe.instructions}</p>
          <button onClick={handleOrder} style={{ marginTop: '30px', width: '100%', padding: '15px', background: '#e67e22', color: 'white', border: 'none', borderRadius: '8px', fontSize: '18px', cursor: 'pointer' }}>
            üõí Order Ingredients Kit
          </button>
        </div>
      </div>
    </div>
  );
};

export default RecipeDetail;
