import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom'; // <--- 1. Import useNavigate

const Login = ({ setToken }) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [isRegistering, setIsRegistering] = useState(false);
  const [name, setName] = useState('');

  const navigate = useNavigate(); // <--- 2. Activate the Hook

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (isRegistering) {
        // REGISTER LOGIC
        const res = await axios.post('http://localhost:5000/api/users/register', { name, email, password });
        localStorage.setItem('token', res.data.token);
        localStorage.setItem('userId', res.data.user.id);
        localStorage.setItem('name', res.data.user.name);
        setToken(res.data.token);
        alert("Registration Successful!");
        navigate('/'); // <--- 3. Redirect to Home
      } else {
        // LOGIN LOGIC
        const res = await axios.post('http://localhost:5000/api/users/login', { email, password });
        localStorage.setItem('token', res.data.token);
        localStorage.setItem('userId', res.data.user.id);
        localStorage.setItem('name', res.data.user.name);
        setToken(res.data.token);
        // alert("Login Successful!"); 
        navigate('/'); // <--- 3. Redirect to Home
      }
    } catch (err) {
      console.error(err);
      alert(err.response?.data?.msg || 'An error occurred');
    }
  };

  return (
    <div style={{ maxWidth: '400px', margin: '50px auto', padding: '30px', border: '1px solid #ddd', borderRadius: '10px', boxShadow: '0 4px 10px rgba(0,0,0,0.1)' }}>
      <h2 style={{ textAlign: 'center', color: '#2c3e50' }}>{isRegistering ? 'Create Account' : 'Welcome Back'}</h2>
      
      <form onSubmit={handleSubmit} style={{ display: 'flex', flexDirection: 'column', gap: '15px' }}>
        
        {isRegistering && (
          <input 
            type="text" 
            placeholder="Full Name" 
            value={name} 
            onChange={(e) => setName(e.target.value)} 
            required 
            style={{ padding: '10px', borderRadius: '5px', border: '1px solid #ccc' }}
          />
        )}

        <input 
          type="email" 
          placeholder="Email Address" 
          value={email} 
          onChange={(e) => setEmail(e.target.value)} 
          required 
          style={{ padding: '10px', borderRadius: '5px', border: '1px solid #ccc' }}
        />
        
        <input 
          type="password" 
          placeholder="Password" 
          value={password} 
          onChange={(e) => setPassword(e.target.value)} 
          required 
          style={{ padding: '10px', borderRadius: '5px', border: '1px solid #ccc' }}
        />

        <button 
          type="submit" 
          style={{ padding: '12px', background: '#27ae60', color: 'white', border: 'none', borderRadius: '5px', cursor: 'pointer', fontWeight: 'bold', fontSize: '16px' }}
        >
          {isRegistering ? 'Sign Up' : 'Login'}
        </button>
      </form>

      <p 
        onClick={() => setIsRegistering(!isRegistering)} 
        style={{ textAlign: 'center', color: '#3498db', cursor: 'pointer', marginTop: '15px', fontSize: '14px' }}
      >
        {isRegistering ? 'Already have an account? Login' : 'New here? Create an account'}
      </p>
    </div>
  );
};

export default Login;
