import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const Admin = () => {
  const [recipes, setRecipes] = useState([]);
  const navigate = useNavigate();

  // 1. Simple Check: Just verify you are logged in
  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      alert("üîí Please login first.");
      navigate('/login'); 
    } else {
      // If logged in, fetch data immediately
      fetchRecipes();
    }
  }, [navigate]);

  // 2. Fetch all recipes
  const fetchRecipes = async () => {
    try {
      const res = await axios.get('http://localhost:5000/api/recipes');
      setRecipes(res.data);
    } catch (err) {
      console.error("Error fetching recipes:", err);
    }
  };

  // 3. Delete a Recipe
  const handleDelete = async (id) => {
    // Confirm before deleting
    if(!window.confirm("Are you sure you want to delete this recipe permanently?")) return;

    try {
      const token = localStorage.getItem('token');
      // Send the token so the backend knows who we are
      const config = { headers: { 'x-auth-token': token } };
      
      await axios.delete(`http://localhost:5000/api/recipes/${id}`, config);
      
      alert("‚úÖ Recipe Deleted Successfully!");
      fetchRecipes(); // Refresh the list automatically
    } catch (err) {
      console.error(err);
      alert("‚ùå Delete Failed. Check backend console.");
    }
  };

  return (
    <div style={{ padding: '30px', maxWidth: '1000px', margin: '0 auto', fontFamily: 'Arial, sans-serif' }}>
      
      {/* Header */}
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '30px' }}>
        <h1 style={{ color: '#2c3e50' }}>üç≥ Recipe Manager</h1>
        <button onClick={() => navigate('/')} style={{ padding: '10px 20px', cursor: 'pointer', background: '#ecf0f1', border: 'none', borderRadius: '5px' }}>
          ‚Üê Back to Menu
        </button>
      </div>

      {/* Recipe List Panel */}
      <div style={{ background: 'white', borderRadius: '10px', boxShadow: '0 4px 15px rgba(0,0,0,0.1)', padding: '20px' }}>
        <h3 style={{ borderBottom: '2px solid #eee', paddingBottom: '10px', marginTop: 0 }}>
          Current Menu ({recipes.length} dishes)
        </h3>
        
        {recipes.length === 0 ? (
          <p style={{ padding: '20px', color: '#777', textAlign: 'center' }}>No recipes found. Add some via Postman!</p>
        ) : (
          <table style={{ width: '100%', borderCollapse: 'collapse', marginTop: '15px' }}>
            <thead>
              <tr style={{ background: '#f8f9fa', textAlign: 'left', color: '#555' }}>
                <th style={{ padding: '12px', borderRadius: '5px 0 0 5px' }}>Image</th>
                <th style={{ padding: '12px' }}>Title</th>
                <th style={{ padding: '12px' }}>Category</th>
                <th style={{ padding: '12px', textAlign: 'right', borderRadius: '0 5px 5px 0' }}>Actions</th>
              </tr>
            </thead>
            <tbody>
              {recipes.map((recipe) => (
                <tr key={recipe._id} style={{ borderBottom: '1px solid #eee' }}>
                  
                  {/* Image Column */}
                  <td style={{ padding: '12px' }}>
                    <img 
                      src={recipe.image || "https://via.placeholder.com/50"} 
                      alt="mini" 
                      style={{ width: '60px', height: '60px', objectFit: 'cover', borderRadius: '8px' }} 
                    />
                  </td>
                  
                  {/* Title Column */}
                  <td style={{ padding: '12px', fontWeight: 'bold', color: '#333' }}>
                    {recipe.title}
                  </td>
                  
                  {/* Category Column */}
                  <td style={{ padding: '12px' }}>
                    <span style={{ 
                      background: recipe.dietaryCategory === 'Veg' ? '#e8f8f5' : '#fdedec', 
                      color: recipe.dietaryCategory === 'Veg' ? '#2ecc71' : '#e74c3c', 
                      padding: '5px 12px', 
                      borderRadius: '20px', 
                      fontSize: '12px', 
                      fontWeight: 'bold',
                      border: recipe.dietaryCategory === 'Veg' ? '1px solid #2ecc71' : '1px solid #e74c3c'
                    }}>
                      {recipe.dietaryCategory}
                    </span>
                  </td>
                  
                  {/* Delete Button Column */}
                  <td style={{ padding: '12px', textAlign: 'right' }}>
                    <button 
                      onClick={() => handleDelete(recipe._id)}
                      style={{ 
                        background: '#e74c3c', 
                        color: 'white', 
                        border: 'none', 
                        padding: '8px 15px', 
                        borderRadius: '5px', 
                        cursor: 'pointer',
                        fontWeight: 'bold',
                        transition: '0.2s'
                      }}
                      onMouseOver={(e) => e.target.style.background = '#c0392b'}
                      onMouseOut={(e) => e.target.style.background = '#e74c3c'}
                    >
                      Delete üóëÔ∏è
                    </button>
                  </td>

                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
};

export default Admin;
