import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import './RecipeList.css'; 

const RecipeList = () => {
  const [recipes, setRecipes] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [filterType, setFilterType] = useState("All"); 
  
  // Try to get the real name, or default to "Chef"
  const userName = localStorage.getItem('name') || "Chef";

  // --- NEW: CHECK LOGIN STATUS ---
  const isLoggedIn = !!localStorage.getItem('token');

  useEffect(() => {
    const fetchRecipes = async () => {
      try {
        const res = await axios.get('http://localhost:5000/api/recipes');
        setRecipes(res.data);
      } catch (err) {
        console.error("Error fetching recipes:", err);
      }
    };
    fetchRecipes();
  }, []);

  const filteredRecipes = recipes.filter((recipe) => {
    const matchesSearch = recipe.title.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesType = filterType === "All" || recipe.dietaryCategory === filterType;
    return matchesSearch && matchesType;
  });

  return (
    <div style={{ fontFamily: 'Arial, sans-serif' }}>
      
      {/* --- NEW: HERO SECTION (Login / Profile Buttons) --- */}
      <div style={{ 
        background: 'linear-gradient(to right, #ffecd2 0%, #fcb69f 100%)', 
        padding: '50px 20px', 
        textAlign: 'center',
        marginBottom: '20px'
      }}>
        <h1 style={{ color: '#2c3e50', fontSize: '2.5rem', margin: '0 0 10px 0' }}>ü•° Meal Kit Delivery</h1>
        <p style={{ color: '#555', fontSize: '1.2rem', marginBottom: '25px' }}>Fresh ingredients. Chef-designed recipes. Delivered.</p>
        
        <div style={{ display: 'flex', justifyContent: 'center', gap: '20px' }}>
          {!isLoggedIn ? (
            // OPTION A: GUEST -> SHOW LOGIN BUTTON
            <Link to="/login">
              <button style={{ padding: '15px 30px', fontSize: '18px', background: '#e67e22', color: 'white', border: 'none', borderRadius: '30px', cursor: 'pointer', fontWeight: 'bold', boxShadow: '0 4px 15px rgba(230, 126, 34, 0.4)' }}>
                üë®‚Äçüç≥ Chef Login
              </button>
            </Link>
          ) : (
             // OPTION B: USER -> SHOW PROFILE BUTTON
            <Link to="/profile">
              <button style={{ padding: '15px 30px', fontSize: '18px', background: '#2c3e50', color: 'white', border: 'none', borderRadius: '30px', cursor: 'pointer', fontWeight: 'bold', boxShadow: '0 4px 15px rgba(44, 62, 80, 0.4)' }}>
                üë§ My Profile
              </button>
            </Link>
          )}
        </div>
      </div>

      {/* --- MAIN CONTENT CONTAINER --- */}
      <div style={{ padding: '30px' }}>

        {/* --- HEADER --- */}
        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '30px' }}>
          <h2 style={{ color: '#2c3e50', margin: 0 }}>Today's Menu üç≤</h2>
          
          {/* Profile Section: ONLY SHOW IF LOGGED IN (To avoid confusion) */}
          {isLoggedIn && (
            <Link to="/profile" style={{ textDecoration: 'none' }}>
              <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', gap: '5px', cursor: 'pointer' }}>
                <img 
                  src="https://cdn-icons-png.flaticon.com/512/3135/3135715.png" 
                  alt="Profile" 
                  className="profile-avatar" 
                />
                <span style={{ color: '#555', fontSize: '13px', fontWeight: 'bold' }}>
                  {userName}
                </span>
              </div>
            </Link>
          )}
        </div>

        {/* --- SEARCH & FILTER BAR (Kept Exactly as is) --- */}
        <div style={{ marginBottom: '30px', display: 'flex', gap: '10px', alignItems: 'center', flexWrap: 'wrap' }}>
          <input 
            type="text" 
            placeholder="Search recipes..." 
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            style={{ padding: '10px', width: '250px', borderRadius: '5px', border: '1px solid #ccc', fontSize: '16px' }}
          />
          <button onClick={() => setFilterType("All")} style={{ padding: '10px 20px', borderRadius: '5px', border: 'none', cursor: 'pointer', background: filterType === "All" ? '#333' : '#ddd', color: filterType === "All" ? 'white' : 'black' }}>All</button>
          <button onClick={() => setFilterType("Veg")} style={{ padding: '10px 20px', borderRadius: '5px', border: 'none', cursor: 'pointer', background: filterType === "Veg" ? '#2ecc71' : '#ddd', color: filterType === "Veg" ? 'white' : 'black' }}>Veg</button>
          <button onClick={() => setFilterType("Non-Veg")} style={{ padding: '10px 20px', borderRadius: '5px', border: 'none', cursor: 'pointer', background: filterType === "Non-Veg" ? '#e74c3c' : '#ddd', color: filterType === "Non-Veg" ? 'white' : 'black' }}>Non-Veg</button>
        </div>

        {/* --- RECIPE GRID (Kept Exactly as is) --- */}
        <div className="recipe-grid">
          {filteredRecipes.length === 0 ? (
            <p style={{ fontSize: '18px', color: '#777' }}>No recipes found matching your search. üòï</p>
          ) : (
            filteredRecipes.map((recipe) => (
              <div key={recipe._id} className="recipe-card">
                <img 
                  src={recipe.image || "https://images.unsplash.com/photo-1546069901-ba9599a7e63c?w=500&auto=format&fit=crop&q=60"} 
                  alt="Food" 
                  className="recipe-image" 
                />
                <h3 className="recipe-title">{recipe.title}</h3>
                <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                  <span className="diet-tag">{recipe.dietaryCategory}</span>
                  <span style={{ fontSize: '14px', color: '#777' }}>‚è≥ {recipe.cookingTime} min</span>
                </div>
                
                <Link to={`/recipe/${recipe._id}`} style={{ textDecoration: 'none' }}>
                  <button className="action-btn btn-green">
                    View Recipe
                  </button>
                </Link>
              </div>
            ))
          )}
        </div>

      </div>
    </div>
  );
};

export default RecipeList;
