import React, { useState } from 'react';
import { Routes, Route, Navigate } from 'react-router-dom'; // Import Router tools
import Login from './components/Login';
import RecipeList from './components/RecipeList';
// We will create this 'RecipeDetail' component in the next step!
import RecipeDetail from './components/RecipeDetail'; 

function App() {
  const [token, setToken] = useState(localStorage.getItem('token'));

  return (
    <div className="App">
      <Routes>
        {/* Route 1: The Home Page (Login or Menu) */}
        <Route 
          path="/" 
          element={!token ? <Login setToken={setToken} /> : <RecipeList setToken={setToken} />} 
        />

        {/* Route 2: The Specific Recipe Page */}
        <Route 
          path="/recipe/:id" 
          element={token ? <RecipeDetail /> : <Navigate to="/" />} 
        />
      </Routes>
    </div>
  );
}

export default App;
