import React from 'react';

const RecipeList = () => {
  // Temporary "Fake" Data
  const recipes = [
    {
      id: 1,
      title: "High-Protein Pasta",
      calories: 450,
      protein: "30g",
      image: "https://via.placeholder.com/150", // Placeholder image
      isVeg: true
    },
    {
      id: 2,
      title: "Quick Chickpea Salad",
      calories: 300,
      protein: "15g",
      image: "https://via.placeholder.com/150",
      isVeg: true
    }
  ];

  return (
    <div style={{ padding: '20px' }}>
      <h2>Today's Menu</h2>
      <div style={{ display: 'flex', gap: '20px' }}>
        {recipes.map((recipe) => (
          <div key={recipe.id} style={{ border: '1px solid #ddd', padding: '15px', borderRadius: '10px', width: '200px' }}>
            <img src={recipe.image} alt={recipe.title} style={{ width: '100%', borderRadius: '5px' }} />
            <h3>{recipe.title}</h3>
            <p><strong>Protein:</strong> {recipe.protein}</p>
            <p><strong>Calories:</strong> {recipe.calories}</p>
            <button style={{ background: '#2ecc71', color: 'white', border: 'none', padding: '10px', borderRadius: '5px', cursor: 'pointer', width: '100%' }}>
              View Recipe
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default RecipeList;
