import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';

const UserProfile = ({ setToken }) => {
  const navigate = useNavigate();
  const [user, setUser] = useState(null);
  const userId = localStorage.getItem('userId');

  useEffect(() => {
    const fetchUserData = async () => {
      if (!userId) return;
      try {
        const res = await axios.get(`http://localhost:5000/api/users/${userId}`);
        setUser(res.data);
      } catch (err) {
        console.error("Error fetching user data:", err);
      }
    };
    fetchUserData();
  }, [userId]);

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('userId');
    localStorage.removeItem('name');
    setToken(null);
    navigate('/'); 
  };

  if (!user) return <div style={{ padding: '50px', textAlign: 'center' }}>Loading Profile...</div>;

  return (
    <div style={{ maxWidth: '600px', margin: '30px auto', fontFamily: 'Arial, sans-serif' }}>
      
      {/* Back Button */}
      <Link to="/" style={{ textDecoration: 'none', color: '#555', display: 'block', marginBottom: '20px' }}>
        ← Back to Menu
      </Link>

      {/* Header */}
      <div style={{ textAlign: 'center', marginBottom: '40px' }}>
        <img 
          src="https://cdn-icons-png.flaticon.com/512/3135/3135715.png" 
          alt="Profile" 
          style={{ width: '100px', height: '100px', borderRadius: '50%', border: '4px solid #2ecc71', marginBottom: '10px' }} 
        />
        <h2 style={{ margin: 0, color: '#2c3e50' }}>{user.name}</h2>
        <p style={{ color: '#777' }}>Master Chef in Training</p>
      </div>

      {/* Favorites Section */}
      <div style={{ background: 'white', borderRadius: '15px', padding: '20px', boxShadow: '0 4px 15px rgba(0,0,0,0.05)' }}>
        <h3 style={{ margin: '0 0 20px 0', borderBottom: '1px solid #eee', paddingBottom: '10px' }}>
          ❤️ My Favorites ({user.favorites.length})
        </h3>

        {user.favorites.length === 0 ? (
          <p style={{ color: '#999', fontStyle: 'italic' }}>You haven't saved any recipes yet. Go explore!</p>
        ) : (
          <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fill, minmax(120px, 1fr))', gap: '15px' }}>
            {/* Map through the Real Favorites */}
            {user.favorites.map((fav) => (
              <Link to={`/recipe/${fav._id}`} key={fav._id} style={{ textDecoration: 'none', color: 'inherit' }}>
                <div style={{ borderRadius: '8px', overflow: 'hidden', border: '1px solid #eee' }}>
                  <img 
                    src="https://images.unsplash.com/photo-1546069901-ba9599a7e63c?w=200&auto=format&fit=crop&q=60" 
                    alt={fav.title}
                    style={{ width: '100%', height: '80px', objectFit: 'cover' }}
                  />
                  <div style={{ padding: '8px', fontSize: '12px', fontWeight: 'bold' }}>
                    {fav.title}
                  </div>
                </div>
              </Link>
            ))}
          </div>
        )}

        <button 
          onClick={handleLogout} 
          style={{ width: '100%', marginTop: '30px', padding: '10px', background: '#e74c3c', color: 'white', border: 'none', borderRadius: '5px', cursor: 'pointer' }}
        >
          Logout
        </button>
      </div>
    </div>
  );
};

export default UserProfile;
