import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import axios from 'axios';
import './RecipeList.css'; 

const RecipeDetail = () => {
  const { id } = useParams();
  const [recipe, setRecipe] = useState(null);

  useEffect(() => {
    const fetchRecipe = async () => {
      try {
        const res = await axios.get(`http://localhost:5000/api/recipes/${id}`);
        setRecipe(res.data);
      } catch (err) {
        console.error("Error fetching recipe:", err);
      }
    };
    fetchRecipe();
  }, [id]);

  if (!recipe) return <div style={{ padding: '20px' }}>Loading...</div>;

  // üëá HELPER: Fixes the YouTube link so it works in the app
  const getEmbedUrl = (url) => {
    if (!url) return null;
    let videoId = null;
    if (url.includes("v=")) {
        videoId = url.split("v=")[1].split("&")[0];
    } else if (url.includes("youtu.be/")) {
        videoId = url.split("youtu.be/")[1].split("?")[0];
    }
    return `https://www.youtube.com/embed/${videoId}`;
  };

  return (
    <div style={{ maxWidth: '800px', margin: '0 auto', padding: '20px', fontFamily: 'Arial, sans-serif' }}>
      
      <Link to="/" style={{ textDecoration: 'none', color: '#555' }}>‚Üê Back to Menu</Link>

      <img 
        src={recipe.image} 
        alt={recipe.title} 
        style={{ width: '100%', height: '300px', objectFit: 'cover', borderRadius: '10px', marginTop: '20px' }} 
      />

      <h1 style={{ color: '#2c3e50' }}>{recipe.title}</h1>
      
      <div style={{ display: 'flex', gap: '15px', color: '#666', marginBottom: '20px' }}>
        <span>‚è± {recipe.cookingTime} mins</span>
        <span>üìä {recipe.difficulty}</span>
        <span>ü•ó {recipe.dietaryCategory}</span>
      </div>

      <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '40px' }}>
        <div>
          <h3 style={{ color: '#e67e22' }}>Ingredients</h3>
          <ul style={{ paddingLeft: '20px' }}>
            {recipe.ingredients.map((ing, index) => (
              <li key={index} style={{ padding: '5px 0' }}>{ing.name}</li>
            ))}
          </ul>
        </div>

        <div>
          <h3 style={{ color: '#e67e22' }}>Instructions</h3>
          <div>
            {recipe.instructions.map((inst, index) => (
              <div key={index} style={{ marginBottom: '15px' }}>
                <strong>Step {inst.step}:</strong> {inst.text}
              </div>
            ))}
          </div>
        </div>
      </div>

      <div style={{ textAlign: 'center', marginTop: '40px', marginBottom: '40px' }}>
        <button style={{ padding: '15px 40px', fontSize: '18px', background: '#27ae60', color: 'white', border: 'none', borderRadius: '30px', cursor: 'pointer' }}>
          üõí Order Meal Kit
        </button>
      </div>

      {/* üëá VIDEO SECTION ADDED HERE (AT THE BOTTOM) üëá */}
      {recipe.youtubeUrl && (
        <div style={{ marginTop: '50px', borderTop: '1px solid #ddd', paddingTop: '30px' }}>
            <h3 style={{ color: '#c4302b', textAlign: 'center' }}>üé• Watch How to Cook</h3>
            <div style={{ position: 'relative', paddingBottom: '56.25%', height: 0, overflow: 'hidden', borderRadius: '10px', marginTop: '20px' }}>
                <iframe 
                    src={getEmbedUrl(recipe.youtubeUrl)}
                    title="YouTube video player"
                    frameBorder="0"
                    allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture" 
                    allowFullScreen
                    style={{ position: 'absolute', top: 0, left: 0, width: '100%', height: '100%' }}
                ></iframe>
            </div>
        </div>
      )}
      {/* üëÜ END VIDEO SECTION üëÜ */}

    </div>
  );
};

export default RecipeDetail;
