import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';

const UserProfile = ({ setToken }) => {
  const navigate = useNavigate();
  const [user, setUser] = useState(null);
  const [orders, setOrders] = useState([]); 
  const [error, setError] = useState('');
  const [showHistory, setShowHistory] = useState(false);

  const userId = localStorage.getItem('userId');

  const handleLogout = () => {
    localStorage.clear(); 
    setToken(null);
    navigate('/'); 
  };

  useEffect(() => {
    if (!userId) return;

    const fetchUserData = async () => {
      try {
        const res = await axios.get(`http://localhost:5000/api/users/${userId}`);
        setUser(res.data);
      } catch (err) {
        console.error("Error fetching user:", err);
        setError("Could not load profile.");
      }
    };

    const fetchOrders = async () => {
      try {
        const token = localStorage.getItem('token');
        const config = token ? { headers: { 'x-auth-token': token } } : {};
        const res = await axios.get(`http://localhost:5000/api/orders/${userId}`, config);
        setOrders(res.data);
      } catch (err) {
        console.error("Error fetching orders:", err);
      }
    };

    fetchUserData();
    fetchOrders();
  }, [userId]);

  if (!userId) return <div style={{textAlign:'center', padding:'50px'}}><h2>Please Login</h2><Link to="/login">Go to Login</Link></div>;
  if (error) return <div style={{textAlign:'center', padding:'50px', color:'red'}}><h3>{error}</h3><button onClick={handleLogout}>Logout</button></div>;
  if (!user) return <div style={{textAlign:'center', padding:'50px'}}>Loading...</div>;

  return (
    <div style={{ maxWidth: '500px', margin: '30px auto', fontFamily: 'Arial, sans-serif' }}>
      <Link to="/" style={{ textDecoration: 'none', color: '#555', display: 'block', marginBottom: '20px' }}>‚Üê Back to Menu</Link>

      {/* HEADER */}
      <div style={{ textAlign: 'center', marginBottom: '40px' }}>
        <img src="https://cdn-icons-png.flaticon.com/512/3135/3135715.png" alt="Profile" style={{ width: '120px', borderRadius: '50%', marginBottom: '15px' }} />
        <h2 style={{ margin: 0 }}>{user.name}</h2>
        <p style={{ color: '#777' }}>{orders.length} Orders Completed</p>
      </div>

      <div style={{ background: 'white', borderRadius: '15px', boxShadow: '0 4px 15px rgba(0,0,0,0.05)', overflow: 'hidden' }}>
        
        {/* ‚ù§Ô∏è FAVORITES SECTION (Restored) */}
        <div style={{ padding: '20px', borderBottom: '1px solid #eee' }}>
          <h4 style={{ margin: '0 0 15px 0' }}>‚ù§Ô∏è My Favorites</h4>
          {user.favorites && user.favorites.length > 0 ? (
            <div style={{ display: 'flex', gap: '10px', overflowX: 'auto', paddingBottom: '10px' }}>
              {user.favorites.map((fav) => {
                // Skip if data is missing (deleted recipe)
                if (!fav || !fav._id) return null;
                
                return (
                  <Link to={`/recipe/${fav._id}`} key={fav._id} style={{ textDecoration: 'none' }}>
                    <div style={{ width: '80px', flexShrink: 0, textAlign: 'center' }}>
                      <img 
                        src={fav.image || "https://images.unsplash.com/photo-1546069901-ba9599a7e63c?w=150&auto=format&fit=crop&q=60"} 
                        alt={fav.title}
                        style={{ width: '80px', height: '80px', borderRadius: '10px', objectFit: 'cover', marginBottom: '5px' }}
                      />
                      <div style={{ fontSize: '10px', color: '#333', fontWeight: 'bold', whiteSpace: 'nowrap', overflow: 'hidden', textOverflow: 'ellipsis' }}>
                        {fav.title}
                      </div>
                    </div>
                  </Link>
                );
              })}
            </div>
          ) : (
            <p style={{ fontSize: '12px', color: '#999' }}>No favorites yet.</p>
          )}
        </div>

        {/* üìú ORDER HISTORY (Restored Links) */}
        <div style={{ borderBottom: '1px solid #eee' }}>
          <div onClick={() => setShowHistory(!showHistory)} style={{ display: 'flex', alignItems: 'center', padding: '20px', cursor: 'pointer' }}>
            <div style={{ flex: 1 }}><h4 style={{ margin: 0 }}>üìú Order History</h4></div>
            <span style={{ color: '#ccc' }}>{showHistory ? '‚ñ≤' : '‚ñº'}</span>
          </div>

          {showHistory && (
            <div style={{ background: '#fafafa', padding: '0 20px 20px 20px' }}>
              {orders.length > 0 ? (
                 orders.map((order) => (
                    // RESTORED LINK HERE:
                    <Link 
                      to={`/recipe/${order.recipe ? order.recipe._id : '#'}`} 
                      key={order._id}
                      style={{ textDecoration: 'none', color: 'inherit' }}
                    >
                      <div style={{ padding: '10px 0', borderBottom: '1px dashed #ddd', cursor: 'pointer' }}>
                        <div style={{ fontWeight: 'bold', fontSize: '14px', color: '#2c3e50' }}>
                          {order.recipe ? order.recipe.title : "Unknown Recipe"}
                        </div>
                        <div style={{ fontSize: '12px', color: '#999', display: 'flex', justifyContent: 'space-between' }}>
                          <span>{new Date(order.date).toLocaleDateString()}</span>
                          <span style={{ fontWeight: 'bold', color: order.status === 'Delivered' ? 'green' : 'orange' }}>
                            {order.status}
                          </span>
                        </div>
                      </div>
                    </Link>
                 ))
              ) : (<p style={{ textAlign: 'center', fontSize: '13px', color: '#999' }}>No orders found.</p>)}
            </div>
          )}
        </div>

        {/* LOGOUT */}
        <div onClick={handleLogout} style={{ padding: '20px', cursor: 'pointer', background: '#fff5f5', color: '#e74c3c', fontWeight: 'bold' }}>
          üö™ Logout
        </div>

      </div>
    </div>
  );
};

export default UserProfile;
