import { Link } from 'react-router-dom';
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './RecipeList.css';


const RecipeList = ({ setToken }) => {
  const [recipes, setRecipes] = useState([]);
  const [expandedRecipeId, setExpandedRecipeId] = useState(null);
  
  // --- NEW: State for Search and Filter ---
  const [searchTerm, setSearchTerm] = useState("");
  const [filterType, setFilterType] = useState("All"); 

  useEffect(() => {
    const fetchRecipes = async () => {
      try {
        const res = await axios.get('http://localhost:5000/api/recipes');
        setRecipes(res.data);
      } catch (err) {
        console.error("Error fetching recipes:", err);
      }
    };
    fetchRecipes();
  }, []);

  const toggleRecipe = (id) => {
    setExpandedRecipeId(expandedRecipeId === id ? null : id);
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    setToken(null);
  };

  // --- NEW: The Filter Logic ---
  // We filter the 'recipes' list and store the result in 'filteredRecipes'
  const filteredRecipes = recipes.filter((recipe) => {
    // 1. Check if title matches search text (convert both to lowercase to be safe)
    const matchesSearch = recipe.title.toLowerCase().includes(searchTerm.toLowerCase());
    
    // 2. Check if category matches (unless filter is "All")
    const matchesType = filterType === "All" || recipe.dietaryCategory === filterType;

    return matchesSearch && matchesType;
  });

  return (
    <div style={{ padding: '30px', fontFamily: 'Arial, sans-serif' }}>
      
      {/* Header & Logout */}
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '20px' }}>
        <h2 style={{ color: '#2c3e50', margin: 0 }}>Today's Menu üç≤</h2>
        <button onClick={handleLogout} className="action-btn btn-red" style={{ width: 'auto', padding: '10px 20px' }}>
          Logout
        </button>
      </div>

      {/* --- NEW: Search Bar & Filter Buttons --- */}
      <div style={{ marginBottom: '30px', display: 'flex', gap: '10px', alignItems: 'center', flexWrap: 'wrap' }}>
        
        {/* Search Input */}
        <input 
          type="text" 
          placeholder="Search recipes..." 
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          style={{ padding: '10px', width: '250px', borderRadius: '5px', border: '1px solid #ccc', fontSize: '16px' }}
        />

        {/* Filter Buttons */}
        <button 
          onClick={() => setFilterType("All")} 
          style={{ padding: '10px 20px', borderRadius: '5px', border: 'none', cursor: 'pointer', background: filterType === "All" ? '#333' : '#ddd', color: filterType === "All" ? 'white' : 'black' }}
        >
          All
        </button>
        <button 
          onClick={() => setFilterType("Veg")} 
          style={{ padding: '10px 20px', borderRadius: '5px', border: 'none', cursor: 'pointer', background: filterType === "Veg" ? '#2ecc71' : '#ddd', color: filterType === "Veg" ? 'white' : 'black' }}
        >
          Veg
        </button>
        <button 
          onClick={() => setFilterType("Non-Veg")} 
          style={{ padding: '10px 20px', borderRadius: '5px', border: 'none', cursor: 'pointer', background: filterType === "Non-Veg" ? '#e74c3c' : '#ddd', color: filterType === "Non-Veg" ? 'white' : 'black' }}
        >
          Non-Veg
        </button>
      </div>

      {/* Grid of Cards (Now mapping through 'filteredRecipes' instead of 'recipes') */}
      <div className="recipe-grid">
        {filteredRecipes.length === 0 ? (
          <p style={{ fontSize: '18px', color: '#777' }}>No recipes found matching your search. üòï</p>
        ) : (
          filteredRecipes.map((recipe) => (
            <div key={recipe._id} className="recipe-card">
              <img 
                src="https://images.unsplash.com/photo-1546069901-ba9599a7e63c?w=500&auto=format&fit=crop&q=60" 
                alt="Food" 
                className="recipe-image" 
              />
              <h3 className="recipe-title">{recipe.title}</h3>
              <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                <span className="diet-tag">{recipe.dietaryCategory}</span>
                <span style={{ fontSize: '14px', color: '#777' }}>‚è≥ {recipe.cookingTime} min</span>
              </div>
              
              {expandedRecipeId !== recipe._id && (
                <div style={{ background: '#f8f9fa', padding: '8px', borderRadius: '5px', marginTop: '10px', fontSize: '13px' }}>
                  üí™ <strong>Protein:</strong> {recipe.nutrients.protein}
                </div>
              )}

              {expandedRecipeId === recipe._id && (
                <div style={{ textAlign: 'left', marginTop: '15px', borderTop: '1px solid #eee', paddingTop: '10px' }}>
                  <h4 style={{ margin: '5px 0', color: '#2ecc71' }}>Ingredients:</h4>
                  <ul style={{ paddingLeft: '20px', fontSize: '14px', color: '#555' }}>
                    {recipe.ingredients.map((ing, index) => (
                      <li key={index}>{ing.quantity} {ing.name}</li>
                    ))}
                  </ul>
                  <h4 style={{ margin: '10px 0 5px 0', color: '#2ecc71' }}>Steps:</h4>
                  <ol style={{ paddingLeft: '20px', fontSize: '14px', color: '#555' }}>
                    {recipe.instructions.map((step) => (
                      <li key={step.stepNumber}>{step.text}</li>
                    ))}
                  </ol>
                </div>
              )}

              <Link to={`/recipe/${recipe._id}`} style={{ textDecoration: 'none' }}>
                <button className="action-btn btn-green">
                    View Recipe
                </button>
              </Link>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default RecipeList;
