import React, { useState, useEffect } from 'react'; // 1. Import useEffect
import axios from 'axios'; // 2. Import axios to talk to backend

const RecipeList = () => {
  const [recipes, setRecipes] = useState([]); // Start with an empty pantry

  // 3. This runs automatically when the page loads
  useEffect(() => {
    const fetchRecipes = async () => {
      try {
        const res = await axios.get('http://localhost:5000/api/recipes');
        setRecipes(res.data); // Store the real data from MongoDB
        console.log("Data fetched:", res.data);
      } catch (err) {
        console.error("Error fetching recipes:", err);
      }
    };

    fetchRecipes();
  }, []);

  return (
    <div style={{ padding: '20px' }}>
      <h2 style={{ color: '#2ecc71' }}>Today's Menu (Fresh from MongoDB)</h2>
      
      <div style={{ display: 'flex', gap: '20px', flexWrap: 'wrap' }}>
        {recipes.length === 0 ? (
          <p>Loading your healthy meals...</p>
        ) : (
          recipes.map((recipe) => (
            <div key={recipe._id} style={styles.card}>
              {/* Note: using _id because that's what MongoDB calls it */}
              <h3>{recipe.title}</h3>
              <span style={styles.tag}>{recipe.dietaryCategory}</span>
              <p><strong>‚è≥ Time:</strong> {recipe.cookingTime} mins</p>
              
              <div style={styles.nutrients}>
                <p>PROTEIN: {recipe.nutrients.protein}</p>
                <p>CALS: {recipe.nutrients.calories}</p>
              </div>

              <button style={styles.button}>View Ingredients</button>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

// Simple Styles for the card
const styles = {
  card: { border: '1px solid #ddd', padding: '15px', borderRadius: '10px', width: '250px', boxShadow: '0 4px 8px rgba(0,0,0,0.1)' },
  tag: { background: '#eee', padding: '4px 8px', borderRadius: '4px', fontSize: '12px' },
  nutrients: { background: '#f9f9f9', padding: '10px', borderRadius: '5px', margin: '10px 0', fontSize: '14px' },
  button: { background: '#2ecc71', color: 'white', border: 'none', padding: '10px', width: '100%', borderRadius: '5px', cursor: 'pointer' }
};

export default RecipeList;
