import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

const AdminDashboard = () => {
  const [orders, setOrders] = useState([]);

  // Fetch ALL orders on load
  useEffect(() => {
    const fetchAllOrders = async () => {
      try {
        const res = await axios.get('http://localhost:5000/api/orders');
        setOrders(res.data);
      } catch (err) {
        console.error("Error fetching orders:", err);
      }
    };
    fetchAllOrders();
  }, []);

  // Handle Status Change
  const updateStatus = async (orderId, newStatus) => {
    try {
      await axios.put(`http://localhost:5000/api/orders/${orderId}`, { status: newStatus });
      // Update the local list so we see the change immediately
      setOrders(orders.map(order => 
        order._id === orderId ? { ...order, status: newStatus } : order
      ));
    } catch (err) {
      console.error("Error updating status:", err);
    }
  };

  // Helper for Status Colors
  const getStatusColor = (status) => {
    if (status === 'Delivered') return '#2ecc71'; // Green
    if (status === 'Shipped') return '#3498db';   // Blue
    return '#f39c12';                             // Orange (Ordered)
  };

  return (
    <div style={{ padding: '30px', maxWidth: '1000px', margin: '0 auto', fontFamily: 'Arial, sans-serif' }}>
      
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '30px' }}>
        <h1 style={{ color: '#2c3e50', margin: 0 }}>üëÆ‚Äç‚ôÇÔ∏è Chef's Control Room</h1>
        <Link to="/" style={{ textDecoration: 'none', color: '#555', border: '1px solid #ddd', padding: '10px 15px', borderRadius: '5px' }}>
          Back to Site
        </Link>
      </div>

      <div style={{ background: 'white', boxShadow: '0 4px 15px rgba(0,0,0,0.1)', borderRadius: '10px', overflow: 'hidden' }}>
        <table style={{ width: '100%', borderCollapse: 'collapse', textAlign: 'left' }}>
          
          <thead style={{ background: '#f8f9fa', borderBottom: '2px solid #eee' }}>
            <tr>
              <th style={{ padding: '15px' }}>Order ID</th>
              <th style={{ padding: '15px' }}>Customer</th>
              <th style={{ padding: '15px' }}>Dish</th>
              <th style={{ padding: '15px' }}>Date</th>
              <th style={{ padding: '15px' }}>Status</th>
              <th style={{ padding: '15px' }}>Actions</th>
            </tr>
          </thead>

          <tbody>
            {orders.map((order) => (
              <tr key={order._id} style={{ borderBottom: '1px solid #eee' }}>
                <td style={{ padding: '15px', fontSize: '12px', color: '#777' }}>
                  {order._id.substring(order._id.length - 6)}... {/* Show last 6 chars */}
                </td>
                <td style={{ padding: '15px', fontWeight: 'bold' }}>
                  {order.user ? order.user.name : "Unknown"}
                </td>
                <td style={{ padding: '15px' }}>
                  {order.recipe ? order.recipe.title : "Unknown Recipe"}
                </td>
                <td style={{ padding: '15px', color: '#555' }}>
                  {new Date(order.date).toLocaleDateString()}
                </td>
                
                {/* Status Badge */}
                <td style={{ padding: '15px' }}>
                  <span style={{ 
                    background: getStatusColor(order.status), 
                    color: 'white', 
                    padding: '5px 10px', 
                    borderRadius: '15px', 
                    fontSize: '12px',
                    fontWeight: 'bold'
                  }}>
                    {order.status}
                  </span>
                </td>

                {/* Action Buttons */}
                <td style={{ padding: '15px' }}>
                  <select 
                    onChange={(e) => updateStatus(order._id, e.target.value)} 
                    value={order.status}
                    style={{ padding: '5px', borderRadius: '5px', border: '1px solid #ddd' }}
                  >
                    <option value="Ordered">Ordered</option>
                    <option value="Shipped">Shipped</option>
                    <option value="Delivered">Delivered</option>
                  </select>
                </td>
              </tr>
            ))}
          </tbody>

        </table>
      </div>
    </div>
  );
};

export default AdminDashboard;
