import React, { useState } from 'react';
import axios from 'axios';

const Login = ({ setToken }) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      // 1. Send Login Request
      // 1. Send Login Request
      const res = await axios.post('http://localhost:5000/api/users/login', {
        email,
        password
      });

      // 2. Save Token, Name, AND USER ID
      localStorage.setItem('token', res.data.token);
      
      const userName = email.split('@')[0];
      localStorage.setItem('name', userName);

      // --- NEW: Save the User ID ---
      // (We need to make sure your backend Login route returns this! See Step 4 below)
      if (res.data.user && res.data.user.id) {
          localStorage.setItem('userId', res.data.user.id);
      }
      
      setToken(res.data.token);

    } catch (err) {
      console.error(err);
      setError('Invalid email or password. Please try again.');
    }
  };

  return (
    <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh', background: '#f0f2f5', fontFamily: 'Arial, sans-serif' }}>
      <div style={{ background: 'white', padding: '40px', borderRadius: '10px', boxShadow: '0 4px 8px rgba(0,0,0,0.1)', width: '300px', textAlign: 'center' }}>
        
        <h2 style={{ color: '#2c3e50', marginBottom: '20px' }}>Quick Dish üç≥</h2>
        <h4 style={{ color: '#777', marginBottom: '20px' }}>Login</h4>

        {error && <p style={{ color: 'red', fontSize: '14px' }}>{error}</p>}

        <form onSubmit={handleSubmit}>
          <input 
            type="email" 
            placeholder="Email" 
            value={email} 
            onChange={(e) => setEmail(e.target.value)}
            required
            style={{ width: '100%', padding: '10px', marginBottom: '10px', borderRadius: '5px', border: '1px solid #ccc', boxSizing: 'border-box' }}
          />
          <input 
            type="password" 
            placeholder="Password" 
            value={password} 
            onChange={(e) => setPassword(e.target.value)}
            required
            style={{ width: '100%', padding: '10px', marginBottom: '20px', borderRadius: '5px', border: '1px solid #ccc', boxSizing: 'border-box' }}
          />
          <button 
            type="submit" 
            style={{ width: '100%', padding: '10px', background: '#2ecc71', color: 'white', border: 'none', borderRadius: '5px', cursor: 'pointer', fontSize: '16px' }}
          >
            Login
          </button>
        </form>

      </div>
    </div>
  );
};

export default Login;
