import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';

const RecipeDetail = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [recipe, setRecipe] = useState(null);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchRecipe = async () => {
      try {
        const res = await axios.get(`http://localhost:5000/api/recipes/${id}`);
        setRecipe(res.data);
      } catch (err) {
        console.error("Fetch Error:", err);
        setError(err.response?.data?.msg || err.message || "Failed to load recipe");
      }
    };
    fetchRecipe();
  }, [id]);

  const handleOrder = async () => {
    const token = localStorage.getItem('token');
    
    if (!token) {
      // If not logged in, ask them to login
      const confirmLogin = window.confirm("You need to login to place an order. Go to login page?");
      if (confirmLogin) navigate('/login');
      return;
    }

    try {
      const config = { headers: { 'x-auth-token': token } };
      await axios.post('http://localhost:5000/api/orders', { recipeId: id }, config);
      
      // SUCCESS: Alert only, DO NOT redirect.
      alert("‚úÖ Order Placed Successfully! We are preparing your kit.");
      
    } catch (err) {
      alert("‚ùå Order Failed: " + (err.response?.data?.msg || "Server Error"));
    }
  };

  // --- ERROR STATE ---
  if (error) return (
    <div style={{ padding: '50px', textAlign: 'center', color: '#e74c3c' }}>
      <h2>‚ö†Ô∏è Error Loading Recipe</h2>
      <p>{error}</p>
      <button onClick={() => navigate('/')} style={{ padding: '10px', cursor: 'pointer' }}>Back to Menu</button>
    </div>
  );

  // --- LOADING STATE ---
  if (!recipe) return <div style={{ padding: '50px', textAlign: 'center' }}>Loading Deliciousness...</div>;

  return (
    <div style={{ padding: '20px', maxWidth: '800px', margin: '0 auto', fontFamily: 'Arial, sans-serif' }}>
      
      <button onClick={() => navigate('/')} style={{ marginBottom: '20px', padding: '10px 20px', cursor: 'pointer', background: '#f8f9fa', border: '1px solid #ddd', borderRadius: '5px' }}>
        ‚Üê Back to Menu
      </button>

      <div style={{ border: '1px solid #ddd', borderRadius: '15px', overflow: 'hidden', boxShadow: '0 4px 15px rgba(0,0,0,0.1)', background: 'white' }}>
        
        {/* HERO IMAGE */}
        <img 
          src={recipe.image || "https://images.unsplash.com/photo-1546069901-ba9599a7e63c?w=800&auto=format&fit=crop&q=60"} 
          alt={recipe.title} 
          style={{ width: '100%', height: '300px', objectFit: 'cover' }}
        />
        
        <div style={{ padding: '30px' }}>
          
          {/* TITLE & BADGE */}
          <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', flexWrap: 'wrap', gap: '10px' }}>
            <h1 style={{ margin: 0, color: '#2c3e50' }}>{recipe.title}</h1>
            <span style={{ background: '#27ae60', color: 'white', padding: '5px 15px', borderRadius: '20px', fontWeight: 'bold', fontSize: '14px' }}>
              {recipe.dietaryCategory || "Tasty"}
            </span>
          </div>

          <p style={{ color: '#777', marginTop: '10px', fontSize: '16px' }}>
            <strong>‚è≥ Time:</strong> {recipe.cookingTime} mins | <strong>üìä Difficulty:</strong> {recipe.difficulty}
          </p>

          <hr style={{ border: '0', borderTop: '1px solid #eee', margin: '20px 0' }} />

          {/* INGREDIENTS (Safe Mode) */}
          <h3>üìù Ingredients</h3>
          <ul style={{ lineHeight: '1.6', color: '#555' }}>
            {recipe.ingredients.map((ing, index) => (
              <li key={index}>
                {typeof ing === 'object' ? ing.name : ing}
              </li>
            ))}
          </ul>

          {/* INSTRUCTIONS (Safe Mode) */}
          <h3>üç≥ Instructions</h3>
          {Array.isArray(recipe.instructions) ? (
            <ol style={{ lineHeight: '1.6', color: '#555', paddingLeft: '20px' }}>
              {recipe.instructions.map((step, index) => (
                <li key={index} style={{ marginBottom: '10px' }}>
                  {typeof step === 'object' ? step.text : step}
                </li>
              ))}
            </ol>
          ) : (
            <p style={{ lineHeight: '1.6', color: '#555' }}>{recipe.instructions}</p>
          )}

          {/* VIDEO SECTION (Restored) */}
          {recipe.youtubeUrl && (
            <div style={{ marginTop: '40px' }}>
              <h3>üé• Watch How to Cook</h3>
              <div style={{ position: 'relative', paddingBottom: '56.25%', height: 0, overflow: 'hidden', borderRadius: '10px' }}>
                <iframe 
                  style={{ position: 'absolute', top: 0, left: 0, width: '100%', height: '100%' }}
                  src={recipe.youtubeUrl.replace("watch?v=", "embed/")} 
                  title="Recipe Video"
                  frameBorder="0" 
                  allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture" 
                  allowFullScreen
                ></iframe>
              </div>
            </div>
          )}

          {/* ORDER BUTTON */}
          <button 
            onClick={handleOrder}
            style={{ 
              marginTop: '40px', 
              width: '100%', 
              padding: '15px', 
              background: '#e67e22', 
              color: 'white', 
              border: 'none', 
              borderRadius: '8px', 
              fontSize: '18px', 
              fontWeight: 'bold', 
              cursor: 'pointer',
              boxShadow: '0 4px 6px rgba(0,0,0,0.1)',
              transition: 'transform 0.1s'
            }}
            onMouseDown={(e) => e.currentTarget.style.transform = 'scale(0.98)'}
            onMouseUp={(e) => e.currentTarget.style.transform = 'scale(1)'}
          >
            üõí Order Ingredients Kit
          </button>

        </div>
      </div>
    </div>
  );
};

export default RecipeDetail;
