import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';

const UserProfile = ({ setToken }) => {
  const navigate = useNavigate();
  const [user, setUser] = useState(null);
  const [orders, setOrders] = useState([]); 
  
  const [showHistory, setShowHistory] = useState(false);
  const [showSettings, setShowSettings] = useState(false);

  const userId = localStorage.getItem('userId');

  // --- 1. THE GUEST CHECK (Fixes the infinite loading) ---
  if (!userId) {
    return (
      <div style={{ maxWidth: '400px', margin: '50px auto', textAlign: 'center', fontFamily: 'Arial, sans-serif', padding: '30px', border: '1px solid #ddd', borderRadius: '15px', boxShadow: '0 4px 10px rgba(0,0,0,0.1)' }}>
        <img 
          src="https://cdn-icons-png.flaticon.com/512/2919/2919600.png" 
          alt="Guest" 
          style={{ width: '100px', marginBottom: '20px', opacity: 0.8 }} 
        />
        <h2 style={{ color: '#2c3e50' }}>Hello, Guest!</h2>
        <p style={{ color: '#777', marginBottom: '30px' }}>
          Log in to view your order history, save favorites, and level up your Chef Rank!
        </p>
        
        <Link to="/login" style={{ textDecoration: 'none' }}>
          <button style={{ 
            background: '#2ecc71', 
            color: 'white', 
            border: 'none', 
            padding: '12px 25px', 
            borderRadius: '5px', 
            fontSize: '16px', 
            cursor: 'pointer', 
            fontWeight: 'bold',
            width: '100%'
          }}>
            Login / Create Account
          </button>
        </Link>
        
        <div style={{ marginTop: '20px' }}>
          <Link to="/" style={{ color: '#555', textDecoration: 'none', fontSize: '14px' }}>
            ‚Üê Back to Menu
          </Link>
        </div>
      </div>
    );
  }

  // --- 2. AUTHENTICATED LOGIC (Only runs if userId exists) ---
  // eslint-disable-next-line react-hooks/rules-of-hooks
  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const res = await axios.get(`http://localhost:5000/api/users/${userId}`);
        setUser(res.data);
      } catch (err) {
        console.error("Error fetching user data:", err);
      }
    };

    const fetchOrders = async () => {
      try {
        const res = await axios.get(`http://localhost:5000/api/orders/${userId}`);
        setOrders(res.data);
      } catch (err) {
        console.error("Error fetching orders:", err);
      }
    };

    fetchUserData();
    fetchOrders();
  }, [userId]);

  const handleLogout = () => {
    localStorage.clear(); 
    setToken(null);
    navigate('/'); 
  };

  const getChefRank = (orderCount) => {
    if (orderCount >= 30) return { title: "Master Chef in Grandmaster üî¥", color: "#FF0000" };
    if (orderCount >= 20) return { title: "Master Chef in Candidate Master üü£", color: "#AA00AA" };
    if (orderCount >= 10) return { title: "Master Chef in Expert üîµ", color: "#0000FF" };
    if (orderCount >= 6)  return { title: "Master Chef in Specialist üí†", color: "#03A89E" };
    if (orderCount >= 3)  return { title: "Master Chef in Pupil üü¢", color: "#008000" };
    return { title: "Master Chef in Newbie üîò", color: "#808080" };
  };

  const getStatusColor = (status) => {
    if (status === 'Delivered') return '#2ecc71'; 
    if (status === 'Shipped') return '#3498db';   
    return '#f39c12';                             
  };

  if (!user) return <div style={{ padding: '50px', textAlign: 'center' }}>Loading Profile...</div>;

  const rank = getChefRank(orders.length);

  return (
    <div style={{ maxWidth: '500px', margin: '30px auto', fontFamily: 'Arial, sans-serif' }}>
      
      <Link to="/" style={{ textDecoration: 'none', color: '#555', display: 'block', marginBottom: '20px' }}>
        ‚Üê Back to Menu
      </Link>

      <div style={{ textAlign: 'center', marginBottom: '40px' }}>
        <img 
          src="https://cdn-icons-png.flaticon.com/512/3135/3135715.png" 
          alt="Profile" 
          style={{ width: '120px', height: '120px', borderRadius: '50%', border: `4px solid ${rank.color}`, marginBottom: '15px' }} 
        />
        <h2 style={{ margin: 0, color: rank.color }}>{user.name}</h2>
        <p style={{ color: rank.color, fontWeight: 'bold', marginTop: '5px' }}>{rank.title}</p>
        <p style={{ fontSize: '12px', color: '#999', margin: 0 }}>{orders.length} Orders Completed</p>
      </div>

      <div style={{ background: 'white', borderRadius: '15px', boxShadow: '0 4px 15px rgba(0,0,0,0.05)', overflow: 'hidden' }}>
        
        {/* Favorites */}
        <div style={{ padding: '20px', borderBottom: '1px solid #eee' }}>
          <div style={{ display: 'flex', alignItems: 'center', marginBottom: '15px' }}>
            <span style={{ fontSize: '20px' }}>‚ù§Ô∏è</span>
            <div style={{ marginLeft: '15px' }}>
              <h4 style={{ margin: 0 }}>My Favorites</h4>
            </div>
          </div>
          {user.favorites && user.favorites.length > 0 ? (
            <div style={{ display: 'flex', gap: '10px', overflowX: 'auto', paddingBottom: '10px' }}>
              {user.favorites.map((fav) => (
                fav ? (
                  <Link to={`/recipe/${fav._id}`} key={fav._id} style={{ textDecoration: 'none' }}>
                    <div style={{ width: '80px', flexShrink: 0, textAlign: 'center' }}>
                      <img 
                        src="https://images.unsplash.com/photo-1546069901-ba9599a7e63c?w=150&auto=format&fit=crop&q=60" 
                        alt={fav.title}
                        style={{ width: '80px', height: '80px', borderRadius: '10px', objectFit: 'cover', marginBottom: '5px' }}
                      />
                      <div style={{ fontSize: '10px', color: '#333', fontWeight: 'bold', whiteSpace: 'nowrap', overflow: 'hidden', textOverflow: 'ellipsis' }}>
                        {fav.title}
                      </div>
                    </div>
                  </Link>
                ) : null
              ))}
            </div>
          ) : (
            <p style={{ margin: '0 0 0 40px', fontSize: '12px', color: '#999' }}>No favorites yet.</p>
          )}
        </div>

        {/* Order History */}
        <div style={{ borderBottom: '1px solid #eee' }}>
          <div 
            onClick={() => setShowHistory(!showHistory)} 
            style={{ display: 'flex', alignItems: 'center', padding: '20px', cursor: 'pointer', transition: 'background 0.2s' }}
            onMouseOver={(e) => e.currentTarget.style.background = '#f9f9f9'}
            onMouseOut={(e) => e.currentTarget.style.background = 'white'}
          >
            <span style={{ fontSize: '20px' }}>üìú</span>
            <div style={{ marginLeft: '15px', flex: 1 }}>
              <h4 style={{ margin: 0 }}>Order History</h4>
              <p style={{ margin: 0, fontSize: '12px', color: '#999' }}>
                {showHistory ? "Click to close" : "Click to view past meals"}
              </p>
            </div>
            <span style={{ color: '#ccc', transform: showHistory ? 'rotate(90deg)' : 'rotate(0deg)', transition: 'transform 0.2s' }}>‚Ä∫</span>
          </div>

          {showHistory && (
            <div style={{ background: '#fafafa', padding: '0 20px 20px 20px' }}>
              {orders.length > 0 ? (
                 <div style={{ display: 'flex', flexDirection: 'column', gap: '0px' }}>
                    {orders.map((order) => (
                      <Link 
                        to={`/recipe/${order.recipe ? order.recipe._id : '#'}`} 
                        key={order._id}
                        style={{ textDecoration: 'none', color: 'inherit' }}
                      >
                        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', padding: '12px 0', borderBottom: '1px dashed #ddd', cursor: 'pointer' }}>
                          <div style={{ display: 'flex', alignItems: 'center', gap: '10px' }}>
                            <span style={{ fontSize: '16px' }}>üì¶</span>
                            <div>
                              <div style={{ fontWeight: 'bold', color: '#333', fontSize: '14px' }}>
                                {order.recipe ? order.recipe.title : "Mystery Recipe"}
                              </div>
                              <div style={{ fontSize: '11px', color: '#999' }}>
                                {new Date(order.date).toLocaleDateString()}
                              </div>
                            </div>
                          </div>
                          
                          <span style={{ 
                            background: getStatusColor(order.status), 
                            color: 'white', 
                            padding: '3px 8px', 
                            borderRadius: '10px', 
                            fontSize: '11px',
                            fontWeight: 'bold'
                          }}>
                            {order.status || "Ordered"}
                          </span>

                        </div>
                      </Link>
                    ))}
                 </div>
              ) : (
                <p style={{ textAlign: 'center', fontSize: '13px', color: '#999', marginTop: '10px' }}>No orders found.</p>
              )}
            </div>
          )}
        </div>

        {/* Settings */}
        <div style={{ borderBottom: '1px solid #eee' }}>
          <div onClick={() => setShowSettings(!showSettings)} style={{ display: 'flex', alignItems: 'center', padding: '20px', cursor: 'pointer' }}>
            <span style={{ fontSize: '20px' }}>‚öôÔ∏è</span>
            <div style={{ marginLeft: '15px', flex: 1 }}>
              <h4 style={{ margin: 0 }}>Settings</h4>
            </div>
            <span style={{ color: '#ccc', transform: showSettings ? 'rotate(90deg)' : 'rotate(0deg)' }}>‚Ä∫</span>
          </div>
          {showSettings && (
            <div style={{ background: '#fafafa', padding: '0 20px 20px 20px' }}>
               <div style={{ padding: '10px 0', borderBottom: '1px dashed #ddd', fontSize: '14px', color: '#555' }}>üîî Notifications</div>
               <div style={{ padding: '10px 0', fontSize: '14px', color: '#555' }}>üîí Privacy</div>
            </div>
          )}
        </div>

        {/* Logout */}
        <div onClick={handleLogout} style={{ display: 'flex', alignItems: 'center', padding: '20px', cursor: 'pointer', background: '#fff5f5' }}>
          <span style={{ fontSize: '20px' }}>üö™</span>
          <div style={{ marginLeft: '15px', flex: 1 }}>
            <h4 style={{ margin: 0, color: '#e74c3c' }}>Logout</h4>
          </div>
        </div>

      </div>
    </div>
  );
};

export default UserProfile;
