import React from 'react';
import { Link } from 'react-router-dom';

const UserProfile = () => {
  return (
    <div style={{ maxWidth: '600px', margin: '50px auto', padding: '20px', textAlign: 'center', fontFamily: 'Arial, sans-serif' }}>
      
      {/* Profile Header */}
      <div style={{ marginBottom: '30px' }}>
        <img 
          src="https://cdn-icons-png.flaticon.com/512/3135/3135715.png" 
          alt="User Profile" 
          style={{ width: '100px', borderRadius: '50%', border: '4px solid #2ecc71' }} 
        />
        <h2 style={{ color: '#2c3e50' }}>Welcome, Chef!</h2>
        <p style={{ color: '#777' }}>Student | Food Lover</p>
      </div>

      {/* Saved Recipes Section (Placeholder for Tomorrow) */}
      <div style={{ border: '1px solid #ddd', borderRadius: '10px', padding: '20px', background: '#f9f9f9' }}>
        <h3 style={{ margin: '0 0 20px 0', color: '#e74c3c' }}>❤️ My Saved Recipes</h3>
        
        <p style={{ color: '#999' }}>You haven't saved any recipes yet.</p>
        
        <Link to="/">
          <button style={{ padding: '10px 20px', background: '#2ecc71', color: 'white', border: 'none', borderRadius: '5px', cursor: 'pointer', marginTop: '10px' }}>
            Back to Menu
          </button>
        </Link>
      </div>

    </div>
  );
};

export default UserProfile;
