import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';

const UserProfile = ({ setToken }) => {
  const navigate = useNavigate();
  const [user, setUser] = useState(null);
  const [orders, setOrders] = useState([]); 
  
  // --- NEW: Toggle State for Order History ---
  const [showHistory, setShowHistory] = useState(false);

  const userId = localStorage.getItem('userId');

  useEffect(() => {
    // 1. Fetch User Data
    const fetchUserData = async () => {
      if (!userId) return;
      try {
        const res = await axios.get(`http://localhost:5000/api/users/${userId}`);
        setUser(res.data);
      } catch (err) {
        console.error("Error fetching user data:", err);
      }
    };

    // 2. Fetch Order History
    const fetchOrders = async () => {
      if (!userId) return;
      try {
        const res = await axios.get(`http://localhost:5000/api/orders/${userId}`);
        setOrders(res.data);
      } catch (err) {
        console.error("Error fetching orders:", err);
      }
    };

    fetchUserData();
    fetchOrders();
  }, [userId]);

  const handleLogout = () => {
    localStorage.clear(); 
    setToken(null);
    navigate('/'); 
  };

  if (!user) return <div style={{ padding: '50px', textAlign: 'center' }}>Loading Profile...</div>;

  return (
    <div style={{ maxWidth: '500px', margin: '30px auto', fontFamily: 'Arial, sans-serif' }}>
      
      <Link to="/" style={{ textDecoration: 'none', color: '#555', display: 'block', marginBottom: '20px' }}>
        ‚Üê Back to Menu
      </Link>

      {/* Header */}
      <div style={{ textAlign: 'center', marginBottom: '40px' }}>
        <img 
          src="https://cdn-icons-png.flaticon.com/512/3135/3135715.png" 
          alt="Profile" 
          style={{ width: '120px', height: '120px', borderRadius: '50%', border: '4px solid #2ecc71', marginBottom: '15px' }} 
        />
        <h2 style={{ margin: 0, color: '#2c3e50' }}>{user.name}</h2>
        <p style={{ color: '#777' }}>Master Chef in Training</p>
      </div>

      <div style={{ background: 'white', borderRadius: '15px', boxShadow: '0 4px 15px rgba(0,0,0,0.05)', overflow: 'hidden' }}>
        
        {/* --- FAVORITES SECTION --- */}
        <div style={{ padding: '20px', borderBottom: '1px solid #eee' }}>
          <div style={{ display: 'flex', alignItems: 'center', marginBottom: '15px' }}>
            <span style={{ fontSize: '20px' }}>‚ù§Ô∏è</span>
            <div style={{ marginLeft: '15px' }}>
              <h4 style={{ margin: 0 }}>My Favorites</h4>
            </div>
          </div>
          {user.favorites && user.favorites.length > 0 ? (
            <div style={{ display: 'flex', gap: '10px', overflowX: 'auto', paddingBottom: '10px' }}>
              {user.favorites.map((fav) => (
                fav ? (
                  <Link to={`/recipe/${fav._id}`} key={fav._id} style={{ textDecoration: 'none' }}>
                    <div style={{ width: '80px', flexShrink: 0, textAlign: 'center' }}>
                      <img 
                        src="https://images.unsplash.com/photo-1546069901-ba9599a7e63c?w=150&auto=format&fit=crop&q=60" 
                        alt={fav.title}
                        style={{ width: '80px', height: '80px', borderRadius: '10px', objectFit: 'cover', marginBottom: '5px' }}
                      />
                      <div style={{ fontSize: '10px', color: '#333', fontWeight: 'bold', whiteSpace: 'nowrap', overflow: 'hidden', textOverflow: 'ellipsis' }}>
                        {fav.title}
                      </div>
                    </div>
                  </Link>
                ) : null
              ))}
            </div>
          ) : (
            <p style={{ margin: '0 0 0 40px', fontSize: '12px', color: '#999' }}>No favorites yet.</p>
          )}
        </div>

        {/* --- CLICKABLE ORDER HISTORY --- */}
        <div style={{ borderBottom: '1px solid #eee' }}>
          
          {/* 1. The Header (Clickable) */}
          <div 
            onClick={() => setShowHistory(!showHistory)} 
            style={{ display: 'flex', alignItems: 'center', padding: '20px', cursor: 'pointer', transition: 'background 0.2s' }}
            onMouseOver={(e) => e.currentTarget.style.background = '#f9f9f9'}
            onMouseOut={(e) => e.currentTarget.style.background = 'white'}
          >
            <span style={{ fontSize: '20px' }}>üìú</span>
            <div style={{ marginLeft: '15px', flex: 1 }}>
              <h4 style={{ margin: 0 }}>Order History</h4>
              <p style={{ margin: 0, fontSize: '12px', color: '#999' }}>
                {showHistory ? "Click to close" : "Click to view past meals"}
              </p>
            </div>
            <span style={{ color: '#ccc', transform: showHistory ? 'rotate(90deg)' : 'rotate(0deg)', transition: 'transform 0.2s' }}>‚Ä∫</span>
          </div>

          {/* 2. The List (Shown only if showHistory is TRUE) */}
          {showHistory && (
            <div style={{ background: '#fafafa', padding: '0 20px 20px 20px' }}>
              {orders.length > 0 ? (
                 <div style={{ display: 'flex', flexDirection: 'column', gap: '10px' }}>
                    {orders.map((order) => (
                      <div key={order._id} style={{ 
                        display: 'flex', 
                        justifyContent: 'space-between', 
                        alignItems: 'center',
                        padding: '10px 0', 
                        borderBottom: '1px dashed #ddd' // MATCHING YOUR IMAGE STYLE
                      }}>
                        <div style={{ display: 'flex', alignItems: 'center', gap: '10px' }}>
                          <span style={{ fontSize: '16px' }}>üì¶</span>
                          <span style={{ fontWeight: 'bold', color: '#333', fontSize: '14px' }}>
                            {order.recipe ? order.recipe.title : "Mystery Recipe"}
                          </span>
                        </div>
                        <span style={{ color: '#777', fontSize: '14px' }}>
                          {new Date(order.date).toLocaleDateString()}
                        </span>
                      </div>
                    ))}
                 </div>
              ) : (
                <p style={{ textAlign: 'center', fontSize: '13px', color: '#999', marginTop: '10px' }}>No orders found.</p>
              )}
            </div>
          )}
        </div>

        {/* --- LOGOUT --- */}
        <div onClick={handleLogout} style={{ display: 'flex', alignItems: 'center', padding: '20px', cursor: 'pointer', background: '#fff5f5' }}>
          <span style={{ fontSize: '20px' }}>üö™</span>
          <div style={{ marginLeft: '15px', flex: 1 }}>
            <h4 style={{ margin: 0, color: '#e74c3c' }}>Logout</h4>
          </div>
        </div>

      </div>
    </div>
  );
};

export default UserProfile;
